void clicked()
{
    HMResourceSchedulingAppointment     HMResourceSchedulingAppointmentMarked;

    super();

    if (HMResourceSchedulingAppointment_1_ds.recordsMarked().lastIndex() > 0)
    {
        HMResourceSchedulingAppointmentMarked = HMResourceSchedulingAppointment_1_ds.getFirst(1);
        while (HMResourceSchedulingAppointmentMarked.RecId)
        {
            element.markDelayed(HMResourceSchedulingAppointmentMarked.RecId);
            info(strFmt("@MAZ9472",HMResourceSchedulingAppointmentMarked.AppointmentNumber));
            HMResourceSchedulingAppointmentMarked = HMResourceSchedulingAppointment_1_ds.getNext();
        }
    }
    else
    {
        element.markDelayed(HMResourceSchedulingAppointment_1.RecId);
        info(strFmt("@MAZ9472",HMResourceSchedulingAppointment_1.AppointmentNumber));
    }

    HMResourceSchedulingAppointment_1_ds.executeQuery();
}

