        public void clicked()
        {
            HMAppointmentNumber appointmentNumber = HMResourceSchedulingAppointment_1.AppointmentNumber;

            HMResourceSchedulingAppointment appt;
            HMCase hmcase;
            HMCaseTrans hmcaseTrans;
            HMCaseService caseService = new HMCaseService();

            while select * from appt
				where appt.AppointmentNumber == appointmentNumber
