				&& hmcaseTrans.BillingStatus == HMBillStatus::Paid
				//if(appt)
            {
                if(hmcaseTrans.BillingStatus == HMBillStatus::Paid)
                {
                    caseService.updateCaseTransStatusTmp(hmcaseTrans.RecId,HMServiceStatus::Complete,true);
                }
            }
		}

