public boolean modified()
{
    boolean ret;

    ret = super();
    if (ret)
    {
        if (this.valueStr() == '' && ToDate.valueStr() == '')
            DateSearch.enabled(false);
        else
            DateSearch.enabled(true);
    }
    ToDate.dateValue(this.dateValue());
    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();
    if (ret)
    {
        if (this.valueStr() == '' && FromDate.valueStr() == '')
            DateSearch.enabled(false);
        else
            DateSearch.enabled(true);
    }

    return ret;
}

