public void refreshAppointmentOrder(HMAppointmentInventTmp _hmAppointmentInventTmp)
{
    VendAccount                             vendAccount;
    CustTable                               custTable;
    RecId                                   workerRecId,resourceRecId;
    HMResourceSchedulingPlanningObject      planObject;
    HMResourceMaster                        resourceMaster;
    HMResourceSchedulingAppointment         HMResourceSchedulingAppointment,HMResourceSchedulingAppointmentPrimary;
    HMResourceSchedulingAppointment         appt = element.args().record();
    boolean                                 complementExtrasCovered = false;
    boolean                                 cashSales = false;
    HMResourceSchedulingAppointmentInvent   HMResourceSchedulingAppointmentInventLocal;
    RecordInsertList    recordList = new RecordInsertList(tableNum(HMResourceSchedulingAppointmentInvent));

    select cashSales from custTable where custTable.AccountNum == appt.Client;
    cashSales = custTable.CashSales;

    while select * from _hmAppointmentInventTmp
    {
        HMResourceSchedulingAppointmentInventLocal.InventTable = _hmAppointmentInventTmp.ItemId;
        HMResourceSchedulingAppointmentInventLocal.PatientResponsibility = _hmAppointmentInventTmp.PatientResponsibility;

        if(_hmAppointmentInventTmp.RefRecId == 0)
        {
            HMResourceSchedulingAppointmentInventLocal.PatientResponsibility = HMPatientPaymentResponsibility::Client;
            if(HMInventTable::findByItemId(HMResourceSchedulingAppointmentInventLocal.InventTable).RAMQ == NoYes::Yes)
            {
                HMResourceSchedulingAppointmentInventLocal.PatientResponsibility = HMPatientPaymentResponsibility::Government;
            }
            else if(cashSales && HMResourceSchedulingAppointmentInventLocal.PatientResponsibility != HMPatientPaymentResponsibility::Government)
            {
                HMResourceSchedulingAppointmentInventLocal.PatientResponsibility = HMPatientPaymentResponsibility::Patient;
            }

        }
        if(appt.PrimaryAppointment == YesNo::No)
        {
            select * from HMResourceSchedulingAppointmentPrimary
            where HMResourceSchedulingAppointmentPrimary.HMPatientVisit == appt.HMPatientVisit
            && HMResourceSchedulingAppointmentPrimary.PrimaryAppointment == YesNo::Yes;
            HMResourceSchedulingAppointmentInventLocal.HMResourceSchedulingAppointment = HMResourceSchedulingAppointmentPrimary.RecId;
            HMResourceSchedulingAppointmentInventLocal.RelatedAppointmentRecId = appt.RecId;

        }
        else
        {
            HMResourceSchedulingAppointmentInventLocal.HMResourceSchedulingAppointment = appt.RecId;
        }
        select * from resourceMaster
        join planObject
        join HMResourceSchedulingAppointment
        where resourceMaster.recid == planObject.HMResourceMaster
        && HMResourceSchedulingAppointment.HMResourceSchedulingPlanningObject == planObject.RecId
        && HMResourceSchedulingAppointment.RecId == HMResourceSchedulingAppointmentInventLocal.HMResourceSchedulingAppointment;

        if(resourceMaster)
        {
            if(resourceMaster.type == HMResourceType::Vendor)
            {
                vendAccount = VendTable::findRecId(resourceMaster.RefRecId).AccountNum;
                resourceRecId = resourceMaster.RecId;
            }
            else if(resourceMaster.type == HMResourceType::Employee)
            {
                workerRecId = resourceMaster.RefRecId;
                resourceRecId = resourceMaster.RecId;
            }

        }
        if(HMInventTable::findByItemId(HMResourceSchedulingAppointmentInventLocal.InventTable).MDactivity == NoYes::Yes)
        {
            HMResourceSchedulingAppointmentInventLocal.VendTable = vendAccount;
            HMResourceSchedulingAppointmentInventLocal.HcmWorker = workerRecId;
            HMResourceSchedulingAppointmentInventLocal.HMResourceMaster = resourceRecId;
        }
        recordList.add(HMResourceSchedulingAppointmentInventLocal);
    }
    recordList.insertDatabase();
    HMResourceSchedulingAppointmentInvent_ds.executeQuery();

}

