{
    HMInventTable hmInventTable;
    select ReferenceNumberRequired from hmInventTable
    where hmInventTable.InventTable == _hmResourceSchedulingAppointmentInvent.InventTable;

    return hmInventTable.ReferenceNumberRequired;
}

public boolean validateWrite()
{
    HMInventTable hmInventTable;
    boolean ret;

    ret = super();
    if(ret)
    {
        select ReferenceNumberRequired from hmInventTable
        where hmInventTable.InventTable == HMResourceSchedulingAppointmentInvent.InventTable;

        if(HMResourceSchedulingAppointmentInvent.HMServiceStatus == HMServiceStatus::Complete)
        {
            if(HMResourceSchedulingAppointmentInvent.NotRequired == NoYes::No)
            {
                if(hmInventTable.ReferenceNumberRequired && HMResourceSchedulingAppointmentInvent.DrugReference == '')
                {
                    ret = checkFailed("@MAZ12248");
                }
                //if(hmInventTable.ReferenceNumberRequired && HMResourceSchedulingAppointmentInvent.HMMedSite == 0)
                //{
                    //ret = checkFailed("@MAZ12287");
                //}
            }
        }


    }
    return ret;
}

