public int active()
{
    int ret;
    HMInventTable hmInventTable;
    ret = super();

    if(ret)
    {
        DeclineService.enabled(HMResourceSchedulingAppointmentInvent.HMServiceStatus == HMServiceStatus::NotStarted);
        HMResourceSchedulingUndeclineService.enabled(HMResourceSchedulingAppointmentInvent.HMServiceStatus == HMServiceStatus::Decline);
    }

    if(HMResourceSchedulingAppointmentInvent.HMServiceStatus == HMServiceStatus::NotStarted && HMResourceSchedulingAppointmentInvent.isProtocol())
    {
        HMAppointmentExamCodesExpand.enabled(true);
    }
    else
    {
        HMAppointmentExamCodesExpand.enabled(false);
    }

    element.updateSubStatus();

    return ret;
}

public void init()
{
    HMResourceSchedulingAppointment appt;
    RecId                           visitRecId;
    super();

    select  PrimaryAppointment,RecId, HMPatientVisit from appt
    where appt.RecId == element.args().record().RecId;
    visitRecId = appt.HMPatientVisit;
    if(appt && appt.PrimaryAppointment == YesNo::Yes)
    {
        this.query().dataSourceTable(tableNum(HMResourceSchedulingAppointmentInvent)).addRange(fieldNum(HMResourceSchedulingAppointmentInvent,HMResourceSchedulingAppointment)).value(SysQuery::value(appt.RecId));
    }
    else
    {
        this.query().dataSourceTable(tableNum(HMResourceSchedulingAppointmentInvent)).clearDynalinks();
        this.query().dataSourceTable(tableNum(HMResourceSchedulingAppointmentInvent)).addRange(fieldNum(HMResourceSchedulingAppointmentInvent,RelatedAppointmentRecId)).value(SysQuery::value(appt.RecId));
        select  PrimaryAppointment,RecId, HMPatientVisit from appt
        where appt.HMPatientVisit == visitRecId
        && appt.PrimaryAppointment == YesNo::Yes;
        this.query().dataSourceTable(tableNum(HMResourceSchedulingAppointmentInvent)).addRange(fieldNum(HMResourceSchedulingAppointmentInvent,HMResourceSchedulingAppointment)).value(SysQuery::value(appt.RecId));
    }
    if(element.args().menuItemName() == menuitemDisplayStr(HMAppointmentServiceDialogView))
    {
        this.allowCreate(false);
        this.allowDelete(false);
        this.allowEdit(false);
        ActionPane.visible(false);
    }
    select AppointmentStatus from appt
    where appt.RecId == element.args().record().RecId;
    HMResourceSchedulingAppointmentInvent_ds.object(fieldNum(HMResourceSchedulingAppointmentInvent,HMServiceStatus)).allowEdit(appt.AppointmentStatus != HMResourceSchedulingAppointmentStatus::Confirmed);
}

public void modified()
{
    super();
    if(HMResourceSchedulingAppointmentInvent.HMServiceStatus != HMResourceSchedulingAppointmentInvent.orig().HMServiceStatus)
    {
        if(HMResourceSchedulingAppointmentInvent.HMServiceStatus == HMServiceStatus::Decline)
        {
            checkFailed("@MAZ12291");
            HMResourceSchedulingAppointmentInvent.HMServiceStatus = HMResourceSchedulingAppointmentInvent.orig().HMServiceStatus;
        }
        else if(HMResourceSchedulingAppointmentInvent.HMServiceStatus == HMServiceStatus::NoShow)
        {
            checkFailed(strFmt("@MAZ12357",enum2str(HMServiceStatus::NoShow)));
            HMResourceSchedulingAppointmentInvent.HMServiceStatus = HMResourceSchedulingAppointmentInvent.orig().HMServiceStatus;
        }
        else if(HMResourceSchedulingAppointmentInvent.HMServiceStatus == HMServiceStatus::Cancelled)
        {
            checkFailed(strFmt("@MAZ12357",enum2str(HMServiceStatus::Cancelled)));
            HMResourceSchedulingAppointmentInvent.HMServiceStatus = HMResourceSchedulingAppointmentInvent.orig().HMServiceStatus;
        }
    }
}

