public void lookup()
{
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tablenum(ReasonTable),this,true);
    Query query;
    QueryBuildDataSource queryBuildDataSource;

    sysTableLookup.addLookupfield(fieldnum(ReasonTable,Reason),true);
    sysTableLookup.addLookupfield(fieldnum(ReasonTable,Description));

    query = new Query();

    queryBuildDataSource = query.addDataSource(tablenum(ReasonTable));
    queryBuildDataSource.addSortField(fieldnum(ReasonTable,Reason));

    switch(HMResourceSchedulingAppointmentInvent.HMServiceStatus)
    {
        case HMServiceStatus::Complete:
            queryBuildDataSource.addRange(fieldnum(ReasonTable,ServiceDone)).value(queryvalue(NoYes::Yes));
            break;
        case HMServiceStatus::Decline:
            queryBuildDataSource.addRange(fieldnum(ReasonTable,ServiceDeclined)).value(queryvalue(NoYes::Yes));
            break;
        case HMServiceStatus::Postponed:
            queryBuildDataSource.addRange(fieldnum(ReasonTable,ServicePostponed)).value(queryvalue(NoYes::Yes));
            break;
    }

    sysTableLookup.parmQuery(query);
    sysTableLookup.parmUseLookupValue(true);

    sysTableLookup.performFormLookup();
}
