public boolean modified()
{
    boolean ret;
    CustPaymModeTable       custPaymModeTable;
    ret = super();
    if(ret)
    {
        select RecId,Name,PaymMode from custPaymModeTable where custPaymModeTable.Name == this.text();
        if(custPaymModeTable.RecId)
        {
            paymentMode = custPaymModeTable.PaymMode;
            this.text(custPaymModeTable.Name);
        }
        else
        {
            warning("@MAZ6390");
            this.text('');
        }
    }
    return ret;
}

