public class HMBuildingUnit extends FormRun
{
    #define.LOCATION('Location')
    #define.HOSPITAL('Hospital')

    int64                   ParentID;
    int64                   categoryTypeRecId;
    ImageRes                imageRes;
    recid                   lastSelected;
    TreeItemIdx             lastSelectedIdx;
    boolean                 isNewRecord;
    boolean                 createFromAsset;

    TreeItemIdx             searchEditIdx;
    RecId                   searchEditRecId;
}

public void init()
{
    if(element.args().menuItemName() == menuitemDisplayStr(HMBuildingUnitSearchEdit))
    {
        searchEditRecId = element.args().record().RecId;
    }

    super();
    //Initialize splitter
    this.ParentCall();
    isNewRecord = false;
    createFromAsset = false;

}

private void insertChildData(RecId  _buildingUnitId, HMFixedAssetTmp _hmFixedAssetTmp)
{
    HMBuildingUnit      hmBuildingUnitChild;
    AssetTable          AssetTable;

    while select RecId from HMBuildingUnitChild
        where HMBuildingUnitChild.HMBuildingUnit == _buildingUnitId
    {
        if (HMBuildingUnit::hasChildren(HMBuildingUnitChild.RecId))
        {
           this.insertChildData(HMBuildingUnitChild.RecId, _hmFixedAssetTmp);

            while select RecId, AssetId, Name, HMBuildingUnit from AssetTable
                where AssetTable.HMBuildingUnit == HMBuildingUnitChild.RecId
            {
                _hmFixedAssetTmp.AssetId    = AssetTable.AssetId;
                _hmFixedAssetTmp.Name       = AssetTable.Name;
                _hmFixedAssetTmp.AssetRecId = AssetTable.RecId;
                _hmFixedAssetTmp.Location   = HMBuildingUnit::find(HMBuildingUnitChild.RecId).Description;
                _hmFixedAssetTmp.insert();
            }
        }
        else
        {
            while select RecId, AssetId, Name, HMBuildingUnit from AssetTable
                where AssetTable.HMBuildingUnit == HMBuildingUnitChild.RecId
            {
                _hmFixedAssetTmp.AssetId    = AssetTable.AssetId;
                _hmFixedAssetTmp.Name       = AssetTable.Name;
                _hmFixedAssetTmp.AssetRecId = AssetTable.RecId;
                _hmFixedAssetTmp.Location   = HMBuildingUnit::find(HMBuildingUnitChild.RecId).Description;
                _hmFixedAssetTmp.insert();

            }
        }
    }
}

public void insertTmpData(RecId  _buildingUnitId)
{
    AssetTable          AssetTable;
    HMFixedAssetTmp     HMFixedAssetTmpLocal;

    if (HMBuildingUnit::hasChildren(_buildingUnitId))
    {
        this.insertChildData(_buildingUnitId, HMFixedAssetTmpLocal);
        while select RecId, AssetId, Name, HMBuildingUnit from AssetTable
            where AssetTable.HMBuildingUnit == _buildingUnitId
        {
            HMFixedAssetTmpLocal.AssetId    = AssetTable.AssetId;
            HMFixedAssetTmpLocal.Name       = AssetTable.Name;
            HMFixedAssetTmpLocal.AssetRecId = AssetTable.RecId;
            HMFixedAssetTmpLocal.Location   = HMBuildingUnit::find(_buildingUnitId).Description;
            HMFixedAssetTmpLocal.insert();
        }
    }
    else
    {
        while select RecId, AssetId, Name, HMBuildingUnit from AssetTable
            where AssetTable.HMBuildingUnit == _buildingUnitId
        {
            HMFixedAssetTmpLocal.AssetId    = AssetTable.AssetId;
            HMFixedAssetTmpLocal.Name       = AssetTable.Name;
            HMFixedAssetTmpLocal.AssetRecId = AssetTable.RecId;
            HMFixedAssetTmpLocal.Location   = HMBuildingUnit::find(_buildingUnitId).Description;
            HMFixedAssetTmpLocal.insert();
        }
    }

    HMFixedAssetTmp.setTmpData(HMFixedAssetTmpLocal);
    HMFixedAssetTmp_ds.executeQuery();
}

public void parentCall()
{
    TreeItemIdx                     parentItemIdx;

    Tree.deleteAll();
    parentItemIdx = SysFormTreeControl::addTreeItem(Tree, CompanyInfo::findRecId(CompanyInfo::current()).Name(), FormTreeAdd::Root, 0, imageRes, 1);
    this.treeFill(parentItemIdx, 0);
    Tree.expand(parentItemIdx);

}

public void refresh(RecId  _buildingunitRecId)
{
    createFromAsset = true;
    lastSelected = _buildingunitRecId;
    this.parentCall();
    tree.select(lastSelectedIdx);
}

public void refreshFixedAssetsGrid()
{
    element.insertTmpData(tree.getItem(tree.getSelection()).data());
}

public void run()
{
    super();

    if(element.args().menuItemName() == menuitemDisplayStr(HMBuildingUnitSearchEdit))
    {
        Tree.select(searchEditIdx);
    }
}

private void setParentID(int64 _parentID)
{
    ParentID                        = _parentID;
    HMBuildingUnit.HMBuildingUnit   = ParentID;

}

private void treeFill(TreeItemIdx _RecId,int64 _RecIdx)
{
    TreeItemIdx                     parentItemIdx;
    boolean                         hasChild;
    HMBuildingUnit                  hmBuildingUnitlocalForChildCheck;
    HMBuildingUnit                  hmBuildingUnitlocalForTree;
    hasChild = false;

    while select * from hmBuildingUnitlocalForTree
        where hmBuildingUnitlocalForTree.HMBuildingUnit == _RecIdx
    {
        while select * from hmBuildingUnitlocalForChildCheck
            where hmBuildingUnitlocalForChildCheck.HMBuildingUnit == hmBuildingUnitlocalForTree.RecId
        {
            hasChild = true;
        }

        if(hasChild)
        {
            parentItemIdx = SysFormTreeControl::addTreeItem(Tree,hmBuildingUnitlocalForTree.Description,
                                                                _RecId,hmBuildingUnitlocalForTree.RecId,imageRes,1);
            if(lastSelected == hmBuildingUnitlocalForTree.RecId)
                {
                    lastSelectedIdx = parentItemIdx;
                }
            if(searchEditRecId == hmBuildingUnitlocalForTree.RecId)
            {
                searchEditIdx = parentItemIdx;
            }
            this.treeFill(parentItemIdx,hmBuildingUnitlocalForTree.RecId);
            hasChild = false;
        }
        else
        {
            parentItemIdx = SysFormTreeControl::addTreeItem(Tree,hmBuildingUnitlocalForTree.Description,
                                                                _RecId,hmBuildingUnitlocalForTree.RecId,imageRes,0);
            if(lastSelected == hmBuildingUnitlocalForTree.RecId)
                {
                    lastSelectedIdx = parentItemIdx;
                }
            if(searchEditRecId == hmBuildingUnitlocalForTree.RecId)
            {
                searchEditIdx = parentItemIdx;
            }
        }
    }
}

public void updateDesign()
{

    if (HMBuildingUnitType::find(HMBuildingUnitCategory::find(categoryTypeRecId).HMBuildingUnitType).Indicators == HMBuildingUnitTypeIndicator::Others
            || HMBuildingUnitType::find(HMBuildingUnitCategory::find(categoryTypeRecId).HMBuildingUnitType).Indicators == HMBuildingUnitTypeIndicator::Building)
    {
        GroupValidity.enabled(false);
        GroupPlanning.enabled(false);
        GroupBlocking.enabled(false);
    }
    else
    {
        GroupValidity.enabled(true);
        GroupPlanning.enabled(true);
        GroupBlocking.enabled(true);
    }

    if (HMBuildingUnitType::find(HMBuildingUnitCategory::find(categoryTypeRecId).HMBuildingUnitType).Indicators == HMBuildingUnitTypeIndicator::BedLocation)
    {
        BedType.enabled(true);
        HMBedClass.enabled(true);
        HMBuildingUnit_Census.enabled(true);
    }
    else
    {
        HMBedClass.enabled(false);
        BedType.enabled(false);
        HMBuildingUnit_Census.enabled(false);
    }

    if (HMBuildingUnit.IsBlocked == NoYes::Yes)
    {
        HMBuildingUnit_HMBlockingReasonCode.enabled(true);
    }
    else
    {
         HMBuildingUnit_HMBlockingReasonCode.enabled(false);
    }

    if (HMBuildingUnit.IsGender == NoYes::Yes)
    {
        HMBuildingUnit_Gender.enabled(true);
    }
    else
    {
            HMBuildingUnit.Gender = HcmPersonGender::None;
         HMBuildingUnit_Gender.enabled(false);
    }
}

