public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        if (HMBuildingUnitType::find(HMBuildingUnitCategory::find(HMBuildingUnit.HMBuildingUnitCategory).HMBuildingUnitType).Indicators == HMBuildingUnitTypeIndicator::BedLocation)
        {
            BedType.enabled(true);
            HMBedClass.enabled(true);
            HMBuildingUnit_Census.enabled(true);
        }
        else
        {
            HMBedClass.enabled(false);
            BedType.enabled(false);
            HMBuildingUnit_Census.enabled(false);
        }

    }

    return ret;
}

public void create(boolean _append = false)
{
    super(_append);
    HMBuildingUnit.ToDate = 31\12\2154;
    HMFixedAssetTmp.clear();
    isNewRecord = true;

    HMBuildingUnitDesc.enabled(true);
    HMBuildingUnitDesc.text("");
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if(ret)
    {
        if(HMBuildingUnit.ToDate < HMBuildingUnit.FromDate)
        {
            ret = false;
            Box::info('From date cannot be lesser than To Date');
        }
        if(HMBuildingUnit.HMBuildingUnit == 0)
        {

            if (HMBuildingUnitType::find(HMBuildingUnitCategory::find(HMBuildingUnit.HMBuildingUnitCategory).HMBuildingUnitType).Indicators != HMBuildingUnitTypeIndicator::Others
                    && HMBuildingUnitType::find(HMBuildingUnitCategory::find(HMBuildingUnit.HMBuildingUnitCategory).HMBuildingUnitType).Indicators != HMBuildingUnitTypeIndicator::Building)
            {
                warning('Building unit of category ' + HMBuildingUnitCategory::find(HMBuildingUnit.HMBuildingUnitCategory).Description + ' must be filled in');
            }
        }
    }

    return ret;
}

public void delete()
{
    AssetTable          AssetTableLocal;
    HMFixedAssetTmp     HMFixedAssetTmpLocal    = null;
    RecId               buildingUnitRecId       = HMBuildingUnit.RecId;
    boolean             hasAsset                = false;

    super();
    element.ParentCall();

    if (buildingUnitRecId) // if not a new record
    {
        //  deletes the association of fixed assset with BU
        ttsBegin;
        while select forUpdate AssetTableLocal
            where AssetTableLocal.HMBuildingUnit == buildingUnitRecId
        {
            AssetTableLocal.HMBuildingUnit = 0;
            AssetTableLocal.update();
            hasAsset = true;
        }
        ttsCommit;
        HMFixedAssetTmp.setTmpData(HMFixedAssetTmpLocal);
        HMFixedAssetTmp_ds.executeQuery();
        if (hasAsset)
        {
            info("@MAZ7671");
        }
    }
}

public void write()
{
    if(HMBuildingUnitDesc.valueStr() == "")
    {
        HMBuildingUnit.HMBuildingUnit = 0;
    }
    else
    {
        HMBuildingUnit.HMBuildingUnit = ParentID;
    }

    if (HMBuildingUnitType::find(HMBuildingUnitCategory::find(HMBuildingUnit.HMBuildingUnitCategory).HMBuildingUnitType).Indicators == HMBuildingUnitTypeIndicator::RoomWithBedSupport)
    {
        if(HMBuildingUnit.orig().IsGender != HMBuildingUnit.IsGender)
        {
            if(!HMBuildingUnit.orig().IsGender)
            {
                HMBuildingUnit::updateGenderOccupancy(HMBuildingUnit.RecId,HMBuildingUnit.IsGender, HMBuildingUnit.Gender,false);
            }
            else
            {
                if(Box::yesNo("@MAZ8221",DialogButton::Yes,"@SYS4784") == DialogButton::Yes)
                {
                    HMBuildingUnit::updateGenderOccupancy(HMBuildingUnit.RecId,HMBuildingUnit.IsGender, HMBuildingUnit.Gender,true);
                }
            }
        }
        else if(HMBuildingUnit.orig().IsGender == HMBuildingUnit.IsGender && HMBuildingUnit.IsGender)
        {
            if(HMBuildingUnit.orig().Gender != HMBuildingUnit.Gender)
            {
                if(Box::yesNo("@MAZ8221",DialogButton::Yes,"@SYS4784") == DialogButton::Yes)
                {
                    HMBuildingUnit::updateGenderOccupancy(HMBuildingUnit.RecId,HMBuildingUnit.IsGender, HMBuildingUnit.Gender,true);
                }
            }
        }

        if(HMBuildingUnit.orig().HMTreatmentClassification != HMBuildingUnit.HMTreatmentClassification)
        {
            if(!HMBuildingUnit.orig().HMTreatmentClassification)
            {
                HMBuildingUnit::updateTreatmentClassification(HMBuildingUnit.RecId,HMBuildingUnit.HMTreatmentClassification,false);
            }
            else
            {
                if(Box::yesNo("@MAZ8222",DialogButton::Yes,"@SYS4784") == DialogButton::Yes)
                {
                    HMBuildingUnit::updateTreatmentClassification(HMBuildingUnit.RecId,HMBuildingUnit.HMTreatmentClassification,true);
                }
            }
        }
    }

    super();
    //ParentID=0;
     lastSelected = HMBuildingUnit.RecId;

    element.ParentCall();
    tree.select(lastSelectedIdx);


}

void clicked()
{
    super();
    HMBuildingUnitDesc.enabled(true);
    HMBuildingUnitDesc.text("");
}

public void selectionChanged(FormTreeItem _OldItem, FormTreeItem _NewItem, FormTreeSelect _how)
{
    HMBuildingUnit      hmBuildingUnitLocal;
    str                 unitdec;
    FormTreeItem selectedTreeItem = tree.getItem(tree.getSelection());
    super(_OldItem, _NewItem, _how);

    if(selectedTreeItem)
    {
        select * from hmBuildingUnitLocal
            where hmBuildingUnitLocal.RecId == selectedTreeItem.data();
    }
    //breakpoint;
    HMFixedAssetTmp.clear();

    if (createFromAsset) // added by yasir - to display BU created from asset
    {
        HMBuildingUnit_q.dataSourceTable(tableNum(HMBuildingUnit)).clearRanges();
        HMBuildingUnit_q.dataSourceTable(tableNum(HMBuildingUnit)).addRange(fieldnum(HMBuildingUnit,recId)).value(int642str(hmBuildingUnitLocal.RecId));
        HMBuildingUnit_ds.executeQuery();
        ParentID = HMBuildingUnit.HMBuildingUnit;
        HMBuildingUnitDesc.text(HMBuildingUnit::find(ParentID).Description);
        element.insertTmpData(hmBuildingUnitLocal.RecId);
        createFromAsset = false;
    }
    else
    {
        if(tree.getRoot() == tree.getSelection())
        {
            HMBuildingUnit_q.dataSourceTable(tableNum(HMBuildingUnit)).clearRanges();
            HMBuildingUnit_q.dataSourceTable(tableNum(HMBuildingUnit)).addRange(fieldnum(HMBuildingUnit,recId)).value('0');
            HMBuildingUnit_ds.executeQuery();
            HMBuildingUnitDesc.enabled(false);
            HMBuildingUnitDesc.text("");
            lastSelectedIdx = selectedTreeItem.idx();
        }
        else
        {
            if (tree.getRoot() != lastSelectedIdx)
            {
               // if(selectedTreeItem.idx() != lastSelectedIdx) // 14Feb2013 remove this check by yasir - record selection problem resolved.
                {
                    if(HMBuildingUnit_ds.validateWrite())
                    {
                        HMBuildingUnitDesc.enabled(true);

                        HMBuildingUnit_q.dataSourceTable(tableNum(HMBuildingUnit)).clearRanges();
                        HMBuildingUnit_q.dataSourceTable(tableNum(HMBuildingUnit)).addRange(fieldnum(HMBuildingUnit,recId)).value(int642str(hmBuildingUnitLocal.RecId));
                        //HMBuildingUnit_ds.findRecord(hmBuildingUnitLocal);
                        HMBuildingUnit_ds.executeQuery();

                        categoryTypeRecId = HMBuildingUnit.HMBuildingUnitCategory;
                        unitdec = HMBuildingUnit::find(HMBuildingUnit.HMBuildingUnit).Description;
                        ParentID = HMBuildingUnit.HMBuildingUnit;
                        HMBuildingUnitDesc.text(HMBuildingUnit::find(ParentID).Description);
                        HMBuildingUnit_ds.reread();
                        HMBuildingUnit_ds.refresh();
                        HMBuildingUnit_ds.executeQuery();
                        HMBuildingUnit_Code.update();
                        HMBuildingUnit_Description.update();
                        HMBuildingUnit_ShortName.update();
                        HMBuildingCategoryType.update();

                        HMBuildingUnitDesc.update();
                        HMBuildingUnit_FromDate.update();
                        HMBuildingUnit_ToDate.update();
                        HMBuildingUnit_IsGender.update();
                        HMBuildingUnit_Gender.update();
                        HMBuildingUnit_MaximumCapacity.update();
                        HMBuildingUnit_IsBlocked.update();
                        HMBuildingUnit_HMBlockingReasonCode.update();

                        element.updateDesign();
                        element.insertTmpData(hmBuildingUnitLocal.RecId);
                    }
                    else
                    {
                        tree.select(lastSelectedIdx);
                    }
                }
            }
            else
            {
                HMBuildingUnit_q.dataSourceTable(tableNum(HMBuildingUnit)).clearRanges();
                HMBuildingUnit_q.dataSourceTable(tableNum(HMBuildingUnit)).addRange(fieldnum(HMBuildingUnit,recId)).value(int642str(hmBuildingUnitLocal.RecId));
                //HMBuildingUnit_ds.findRecord(hmBuildingUnitLocal);
                HMBuildingUnit_ds.executeQuery();

                categoryTypeRecId = HMBuildingUnit.HMBuildingUnitCategory;
                unitdec = HMBuildingUnit::find(HMBuildingUnit.HMBuildingUnit).Description;
                ParentID = HMBuildingUnit.HMBuildingUnit;
                HMBuildingUnitDesc.text(HMBuildingUnit::find(ParentID).Description);
                HMBuildingUnit_ds.executeQuery();
                lastSelectedIdx = selectedTreeItem.idx();
                if (!isNewRecord)
                {
                    element.insertTmpData(hmBuildingUnitLocal.RecId);
                }
            }
        }
    }
    //ParentID = HMBuildingUnit.HMBuildingUnit;
    //HMBuildingUnitDesc.text(HMBuildingUnit::find(ParentID).Description);
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        categoryTypeRecId = HMBuildingUnit.HMBuildingUnitCategory;
        element.updateDesign();
    }

    return ret;
}

public void lookup()
{
    Args args = new Args();
    FormRun itemLookUp;

    args.name(formstr(HMBuildingUnitLookup));
    args.caller(element);

    args.parm(HMBuildingCategoryType.valueStr());
    categoryTypeRecId = str2int64(HMBuildingCategoryType.valueStr());
    //args.record(HMBuildingUnit);
    itemLookUp = ClassFactory.formRunClass(args);
    itemLookUp.init();
    this.performFormLookup(itemLookUp);
}

