void clicked()
{
    AssetTable  AssetTableLocal;
    super();

    ttsBegin;
    select forUpdate AssetTableLocal
        where AssetTableLocal.RecId == HMFixedAssetTmp.AssetRecId;

    AssetTableLocal.HMBuildingUnit = 0;
    AssetTableLocal.update();
    ttsCommit;

    element.insertTmpData(tree.getItem(tree.getSelection()).data());
}
