[Form]
public class HMBuildingUnitDialog extends FormRun
{
    recId                                   oraganizationalUnitRecId;
    HMBuildingUnitTmp                       hmBuildingUnitlocal;
    Object                                  formRunObject;
    FormRun                                 formRun;
    NoYes                                   isOkClicked;
    HMOrganizationalUnit                    hmOrganizationalUnit;
    HMOrganizationaCategory                 hmOrganizationaCategory;
    HMBuildingUnit                          hmBuildingUnitlocalForTmp;
    HMBuildingUnitType                      hmBuildingUnitType;
    HMBuildingUnitCategory                  hmBuildingUnitCategory;
    HMBuildingUnitTmp                       hmBuildingUnitTmplocal;
    HMOrganizationalUnit                    hmOrganizationalUnitForAssignementCheck;

}

/// <summary>
///
/// </summary>
public void initHMOrganizationalUnit()
{
    select * from hmOrganizationalUnit
        where hmOrganizationalUnit.RecId == oraganizationalUnitRecId;
}

public void initHMOrganizationaCategory()
{
    select * from hmOrganizationaCategory
        where hmOrganizationaCategory.RecId == str2int64(element.args().parm());
}

public void  createNursingAssignments()
{

    while select * from hmBuildingUnitlocalForTmp
            join RecId, HMBuildingUnitType from hmBuildingUnitCategory
            join RecId,Indicators from hmBuildingUnitType
            where
            hmBuildingUnitlocalForTmp.HMBuildingUnitCategory == hmBuildingUnitCategory.RecId
            && hmBuildingUnitCategory.HMBuildingUnitType == hmBuildingUnitType.RecId
            && hmBuildingUnitType.Indicators == HMBuildingUnitTypeIndicator::RoomWithBedSupport
    {
        select count (RecId) from hmOrganizationalUnitForAssignementCheck
                where hmOrganizationalUnitForAssignementCheck.HMBuildingUnit == hmBuildingUnitlocalForTmp.RecId;
        if(hmOrganizationalUnitForAssignementCheck.RecId==0)
        {
            hmBuildingUnitTmplocal.HMBuildingUnit = hmBuildingUnitlocalForTmp.RecId;
            hmBuildingUnitTmplocal.Description =  hmBuildingUnitlocalForTmp.Description;
            hmBuildingUnitTmplocal.Code = hmBuildingUnitlocalForTmp.Code;
            hmBuildingUnitTmplocal.ShortName = hmBuildingUnitlocalForTmp.ShortName;
            hmBuildingUnitTmplocal.insert();
        }
    }
}

public void createAssignments()
{

    while select * from hmBuildingUnitlocalForTmp
            join RecId,HMBuildingUnitType from hmBuildingUnitCategory
            join RecId,Indicators from hmBuildingUnitType
            where
            hmBuildingUnitlocalForTmp.HMBuildingUnitCategory == hmBuildingUnitCategory.RecId
            && hmBuildingUnitCategory.HMBuildingUnitType == hmBuildingUnitType.RecId
            && hmBuildingUnitType.Indicators == HMBuildingUnitTypeIndicator::TreatmentRoom
    {
        select count (RecId) from hmOrganizationalUnitForAssignementCheck
                where hmOrganizationalUnitForAssignementCheck.HMBuildingUnit == hmBuildingUnitlocalForTmp.RecId;
        if(hmOrganizationalUnitForAssignementCheck.RecId==0)
        {
            hmBuildingUnitTmplocal.HMBuildingUnit = hmBuildingUnitlocalForTmp.RecId;
            hmBuildingUnitTmplocal.Description =  hmBuildingUnitlocalForTmp.Description;
            hmBuildingUnitTmplocal.Code = hmBuildingUnitlocalForTmp.Code;
            hmBuildingUnitTmplocal.ShortName = hmBuildingUnitlocalForTmp.ShortName;
            hmBuildingUnitTmplocal.insert();
        }
    }
}

public void init()
{


    super();
    this.initHMOrganizationalUnit();

    this.initHMOrganizationaCategory();

    if(hmOrganizationaCategory.Indicator == HMOrganizationalCategoryIndicator::NursingAssignments)
    {
        this.createNursingAssignments();
    }
    else if(hmOrganizationaCategory.Indicator == HMOrganizationalCategoryIndicator::TreatmentUnit || hmOrganizationaCategory.Indicator == HMOrganizationalCategoryIndicator::OutpatientTreatment ||
                hmOrganizationaCategory.Indicator == HMOrganizationalCategoryIndicator::OtherUnit)
    {
        this.createAssignments();
    }

    HMBuildingUnitTmp.setTmpData(hmBuildingUnitTmplocal);
}
