private boolean calendarWeekNotExist(RecId _weekPlanRecId)
{
    HMResourceSchedulingWeekPlanToCalender   hmResourceSchedulingWeekPlanToCalenderlocal;
    int i;
    Set weekNotExistList = new Set(Types::Integer);
    Set weekNotCopiedtList = new Set(Types::Integer);
    DialogButton        dialogButton;
    SetEnumerator enumr;
    SetEnumerator enumrNotCopied;
    boolean ret = true;
    boolean isSingleExist = false;
    str weekNums = '';
    str weekNumsNotCopied = '';
    str message = '';
    int currentWeekNum, j;

    for(i=CalenderWeekFrom.value();i<=CalenderWeekTo.value();i++)
    {
        select count(recId) from hmResourceSchedulingWeekPlanToCalenderlocal
                where hmResourceSchedulingWeekPlanToCalenderlocal.HMResourceSchedulingWeekPlan==_weekPlanRecId
                && hmResourceSchedulingWeekPlanToCalenderlocal.WeekNo==i;

        if(HMResourceSchedulingWeekPlanToCalenderlocal.RecId == 0)
        {
            weekNotExistList.add(i);
        }
        else
        {
            isSingleExist = true;
        }
    }

    if(isSingleExist)
    {
        if(weekNotExistList.elements() > 0)
        {
            currentWeekNum=CalenderWeekFrom.value();

            for(j=CalenderWeekFrom1.value();j<=CalenderWeekTo1.value();j++)
            {
                if(weekNotExistList.in(currentWeekNum))
                {
                    weekNotCopiedtList.add(j);
                }

                if(currentWeekNum==CalenderWeekTo.value())
                {
                    currentWeekNum=CalenderWeekFrom.value();
                }
                else
                {
                    currentWeekNum++;
                }
            }

            if(weekNotCopiedtList.elements() == 1)
            {
                enumrNotCopied = weekNotCopiedtList.getEnumerator();
                enumrNotCopied.moveNext();
                weekNumsNotCopied = enumrNotCopied.current();
            }
            else if(weekNotCopiedtList.elements() > 1)
            {
                enumrNotCopied = weekNotCopiedtList.getEnumerator();

                while(enumrNotCopied.moveNext())
                {
                    if(weekNumsNotCopied == "")
                    {
                        weekNumsNotCopied = enumrNotCopied.current();
                    }
                    else
                    {
                        weekNumsNotCopied += strFmt(', %1',enumrNotCopied.current());
                    }
                }
            }

            if(weekNotExistList.elements() == 1)
            {
                enumr = weekNotExistList.getEnumerator();
                enumr.moveNext();

                if(weekNotCopiedtList.elements() == 0)
                {
                    message = strFmt("@MAZ3215",enumr.current());
                }
                else if(weekNotCopiedtList.elements() == 1)
                {
                    message = strFmt("@MAZ3402",enumr.current(),weekNumsNotCopied);
                }
                else if(weekNotCopiedtList.elements() > 1)
                {
                    message = strFmt("@MAZ3401",enumr.current(),weekNumsNotCopied);
                }

                dialogButton = Box::yesNo(message, DialogButton::No, "@SYS301622");
                if(dialogButton == DialogButton::No)
                {
                    ret = false;
                }
            }
            else if(weekNotExistList.elements() > 1)
            {
                enumr = weekNotExistList.getEnumerator();

                while(enumr.moveNext())
                {
                    if(weekNums == "")
                    {
                        weekNums = enumr.current();
                    }
                    else
                    {
                        weekNums += strFmt(", %1",enumr.current());
                    }
                }

                if(weekNotCopiedtList.elements() == 0)
                {
                    message = strFmt("@MAZ3214",weekNums);
                }
                else if(weekNotCopiedtList.elements() == 1)
                {
                    message = strFmt("@MAZ3403",weekNums,weekNumsNotCopied);
                }
                else if(weekNotCopiedtList.elements() > 1)
                {
                    message = strFmt("@MAZ3404",weekNums,weekNumsNotCopied);
                }

                dialogButton = Box::yesNo(message, DialogButton::No, "@SYS301622");
                if(dialogButton == DialogButton::No)
                {
                    ret = false;
                }

            }

/*          if(weekNotExistList.elements() == 1)
            {
                enumr = weekNotExistList.getEnumerator();
                enumr.moveNext();

                dialogButton = Box::yesNo(strFmt("@MAZ3215",enumr.current()), DialogButton::No, "@SYS301622");
                if(dialogButton == DialogButton::No)
                {
                    ret = false;
                }
            }
            else if(weekNotExistList.elements() > 1)
            {
                enumr = weekNotExistList.getEnumerator();

                while(enumr.moveNext())
                {
                    if(weekNums == "")
                    {
                        weekNums = enumr.current();
                    }
                    else
                    {
                        weekNums += strFmt(", %1",enumr.current());
                    }
                }

                dialogButton = Box::yesNo(strFmt("@MAZ3214",weekNums), DialogButton::No, "@SYS301622");
                if(dialogButton == DialogButton::No)
                {
                    ret = false;
                }
            }
*/
        }
    }
    else
    {
        ret = false;

        if(weekNotExistList.elements() == 1)
        {
            enumr = weekNotExistList.getEnumerator();
            enumr.moveNext();

            warning(strFmt("@MAZ3241",enumr.current()));
        }
        else if(weekNotExistList.elements() > 1)
        {
            enumr = weekNotExistList.getEnumerator();

            while(enumr.moveNext())
            {
                if(weekNums == "")
                {
                    weekNums = enumr.current();
                }
                else
                {
                    weekNums += strFmt(', %1',enumr.current());
                }
            }

            warning(strFmt("@MAZ3242",weekNums));
        }
    }

    return ret;
}

public void closeOk()
{
    int flag = 1;

    HMResourceSchedulingHelperObject = new HMResourceSchedulingHelper();

    if(CalenderWeekFrom.value() < 1 || CalenderWeekFrom.value() > 52)
    {
        warning(strFmt("@MAZ3121",1,52));
        flag=0;
    }
    if(CalenderWeekTo.value() < 1 || CalenderWeekTo.value() > 52)
    {
        warning(strFmt("@MAZ3122",1,52));
        flag=0;
    }
    if(CalenderWeekFrom1.value() < 1 || CalenderWeekFrom1.value() > 52)
    {
        warning(strFmt("@MAZ3123",1,52));
        flag=0;
    }
    if(CalenderWeekTo1.value() < 1 || CalenderWeekTo1.value() > 52)
    {
        warning(strFmt("@MAZ3124",1,52));
        flag=0;
    }

    if(flag == 1)
    {
        if (flag == 1 && CalenderWeekFrom.value() > CalenderWeekTo.value())
        {
            warning("@MAZ1935");
            flag=0;
        }

        if (flag == 1 && CalenderWeekFrom1.value() > CalenderWeekTo1.value())
        {
            warning("@MAZ3125");
            flag=0;
        }

        if (flag == 1 && ((CalenderWeekFrom.value() >= CalenderWeekFrom1.value() && CalenderWeekFrom.value() <= CalenderWeekTo1.value()) || (CalenderWeekTo.value() >= CalenderWeekFrom1.value() && CalenderWeekTo.value() <= CalenderWeekTo1.value()) || (CalenderWeekFrom1.value() >= CalenderWeekFrom.value() && CalenderWeekFrom1.value() <= CalenderWeekTo.value()) || (CalenderWeekTo1.value() >= CalenderWeekFrom.value() && CalenderWeekTo1.value() <= CalenderWeekTo.value())))
        {
            warning("@MAZ3213");
            flag=0;
        }
    }

    if (flag == 1 && this.calendarWeekNotExist(element.args().record().RecId))
    {
        HMResourceSchedulingHelperObject.copyCalenderWeek(CalenderWeekFrom.value(), CalenderWeekTo.value(), CalenderWeekFrom1.value(), CalenderWeekTo1.value(), element.args().record().RecId, HMCalendarWeekStatus.selection());
        super();
    }

}
