public void activate(boolean _active)
{
    super(_active);
    if(HMCase.CaseStatus==HMCaseStatus::Open)
    {
        ClaimStatus_Open.enabled(false);
        ClaimStatus_OnHold.enabled(true);
    }
    if(HMCase.CaseStatus==HMCaseStatus::OnHold)
    {
        ClaimStatus_Open.enabled(true);
        ClaimStatus_OnHold.enabled(false);
    }
    if(HMCase.CaseStatus==HMCaseStatus::Billed)
    {
        ClaimStatus_Open.enabled(false);
        ClaimStatus_OnHold.enabled(false);
        RemoveLine.enabled(false);
    }

}

public void closeOk()
{
    element.createCase();
    super();
}

