public void getPatientName(RecId _patient)
{
    boolean   ret  = false;
    HMPatient           hmPatientLocal, pat;
    DirPartyTable       dirPartyTableLocal;
    CustTable           custTableLocal;

    HMPatientRecId = _patient;

    select * from pat where pat.RecId == _patient;

    HMPatient.text(pat.PatientName());

}

