private void updateCaseTrans()
{
    HMCaseTrans hmcaseTrans;
    while select forupdate hmcaseTrans
        where hmcaseTrans.hmcase == HMCase.recid
    {
        hmcaseTrans.PatientResponsibility = HMPatientPaymentResponsibility::Client;
        hmcaseTrans.updateAmountAndDiscount();
        hmcaseTrans.update();
    }
}

