    }

    return ret;
}

public void lookup()
{
   LogisticsAddressState   logisticsAddressState;
   DirPartyTable           dirPartyTableProvider;
   LogisticsPostalAddress  postalAddressProviderDetails;
   Query query = new Query();
   QueryBuildDataSource queryBuildDataSource;
   QueryBuildRange queryBuildRange;
   SysTableLookup sysTableLookup;

   dirPartyTableProvider    =   DirPartyTable::findRec(CompanyInfo::current());
   postalAddressProviderDetails = LogisticsPostalAddress::findByLocation(dirPartyTableProvider.PrimaryAddressLocation);
   queryBuildDataSource = query.addDataSource(tableNum(LogisticsAddressState));
   queryBuildDataSource.addRange(fieldNum(LogisticsAddressState, CountryRegionId)).value(postalAddressProviderDetails.CountryRegionId);
   sysTableLookup = SysTableLookup::newParameters(tableNum(LogisticsAddressState), this);
   sysTableLookup.addLookupfield(fieldNum(LogisticsAddressState, StateId));
   sysTableLookup.addLookupfield(fieldNum(LogisticsAddressState, Name));
   sysTableLookup.parmQuery(query);
   sysTableLookup.performFormLookup();
}

/// <summary>
///
/// </summary>
public void jumpRef()
{
    FormRun formRun;
    Args args = new Args();
    super();
    args.lookupTable(tableNum(HMInsuranceContractCarrierCombination)); //<Master form table which needs to be opened>
        //args.lookupTable(tableNum(ObjectsTable));
        args.lookupField(fieldNum(HMInsuranceContractCarrierCombination,HMInsuranceCarrier));
    //args.lookupField(fieldNum(ObjectsTable,ObjectId));

    args.lookupValue(int2Str(HMPatientInsurance.HMInsuranceCarrier)); //<Current form table field which is related with master form table>
        //args.lookupValue(LeaseTable.UnitId);
    args.lookupRecord(HMInsuranceContractCarrierCombination::findByInsCarrier(HMPatientInsurance.HMInsuranceCarrier));
    //args.lookupRecord(ObjectsTable::fin(LeaseTable.UnitTable));
args.name(formStr(HMInsuranceContractCarrierCombination)); //<Form which needs to be opened>
        //args.name(formStr(ObjectsTable));
    formRun = ClassFactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.wait();
    formRun.detach();
}

