public void updateData()
{
    Query q;
    utcDateTime startdateTime,endDateTime;
    QueryBuildRange appointmentRange, caseRange;
    str caseRangeString;

    str dateTime2strQuery(utcDateTime _utcDateTime)
    {
        str cvtDateTime;

        If (_utcDateTime==DateTimeUtil::minValue())
        cvtDateTime = '1900-01-01T00:00:00';
        else
      cvtDateTime = DateTimeUtil::toStr(_utcDateTime);


        return cvtDateTime;
    }

    startdateTime = DateTimeUtil::newDateTime(StartDate.dateValue(),0);//, Timezone::GMTPLUS0300KUWAIT_RIYADH);
    endDateTime = DateTimeUtil::newDateTime(EndDate.dateValue(),timeMax());//, Timezone::GMTPLUS0300KUWAIT_RIYADH);

    HMCaseTrans_1_ds.query().dataSourceTable(tableNum(HMCaseTrans)).clearRanges();
    HMResourceSchedulingAppointment_1_ds.query().dataSourceTable(tableNum(HMResourceSchedulingAppointment)).clearRanges();

    appointmentRange    = HMResourceSchedulingAppointment_1_ds.query().dataSourceTable(tableNum(HMResourceSchedulingAppointment)).addRange(fieldNum(HMResourceSchedulingAppointment,appointmentDate));
    appointmentRange.value(SysQuery::range(StartDate.dateValue(), EndDate.dateValue()));

    caseRange           =   HMCaseTrans_1_ds.query().dataSourceTable(tableNum(HMCaseTrans)).addRange(fieldNum(HMCaseTrans,CreatedDateTime));
    caseRangeString     =   strFmt(@"(((%3.%5 >= %1) && (%3.%5 <= %2) && (%3.%4 == 0)) || ((%6.%7 >= %1) && (%6.%7 <= %2) && (%3.%4 != 0)))))"
                                , dateTime2strQuery(startDateTime), dateTime2strQuery(endDateTime), "HMCaseTrans_1"
                                , fieldStr(HMCaseTrans, HMResourceSchedulingAppointment), fieldStr(HMCaseTrans, CreatedDateTime)
                                , "HMResourceSchedulingAppointment_1", fieldStr(HMResourceSchedulingAppointment, AppointmentDate));
    caseRange.value(caseRangeString);

    caseRange.status(RangeStatus::Hidden);
    appointmentRange.status(RangeStatus::Hidden);

    q = HMCaseTrans_1_ds.query();
    HMCase_1_ds.executeQuery();

}

public display HMTpaCode getTPA(HMCase _hmcase)
{
    HMPatientInsurance                      hmPatientInsurance = HMPatientInsurance::find(_hmcase.HMPatientInsurance);
    HMInsuranceContractClassOrPlans         plan = HMInsuranceContractClassOrPlans::find(hmPatientInsurance.HMInsuranceContractClassOrPlans);
    HMInsuranceCarrier                      carrier;
    HMInsuranceContract                     contract;
    HMInsuranceContractCarrierCombination   combination;
    HMTPA                                   hMTPA;
    HMTpaDetails                            hMTpaDetails;

    if(plan)
    {
        contract    = HMInsuranceContract::find(plan.HMInsuranceContract);
        combination = HMInsuranceContractCarrierCombination::find(contract.HMInsuranceContractCarrierCombination);
        carrier     = HMInsuranceCarrier::find(combination.HMInsuranceCarrier);

        select firstOnly hMTpaDetails where hMTpaDetails.HMInsuranceCarrier == carrier.RecId;
        return hMTPA::find(hMTpaDetails.Tpa).Code;
    }

    return "";
}

public display str getContractName(HMCaseTrans _hmcaseTrans)
{
    HMCase                                  hmCase;
    HMResourceSchedulingAppointment         appointment;
    HMPatientInsurance                      patientInsurance;
    HMInsuranceContract                     contract;
    HMInsuranceContractClassOrPlans         plan;
    RefRecId                                patientInsuranceRecId;

    str     specialtyDescription;
    boolean isFirst = true;

    if (_hmcaseTrans.PatientResponsibility == HMPatientPaymentResponsibility::Patient)
    {
        return '';
    }

    appointment = HMResourceSchedulingAppointment::find(_hmcaseTrans.HMResourceSchedulingAppointment);

    if (appointment)
    {
        patientInsuranceRecId  = appointment.HMPatientInsurance;
    }
    else
    {
        hmCase                  = hmCase::find(_hmcaseTrans.HMCase);
        patientInsuranceRecId   = hmCase.hmpatientInsurance;
    }

    patientInsurance = HMPatientInsurance::find(patientInsuranceRecId);

    plan        = HMInsuranceContractClassOrPlans::find(patientInsurance.HMInsuranceContractClassOrPlans);
    contract    = HMInsuranceContract::find(plan.HMInsuranceContract);

    return contract.HMContractName;
}

public display TransDate getPerformedDate(HMCaseTrans _hmCaseTrans)
{
    HMPatientChargeEntryLine hMPatientChargeEntryLine;

    select firstOnly PerformedDate from hMPatientChargeEntryLine
    where hMPatientChargeEntryLine.HMCaseTrans == _hmCaseTrans.RecId;
    return hMPatientChargeEntryLine.PerformedDate;
}

public display str getSpecialtyCode(HMCaseTrans _hmcaseTrans)
{
    HMCase                              hmCase;
    HMResourceSchedulingAppointment     appointment;
    HMResourceSchedulingPlanningObject  planningObject;
    HMResourceMaster                    resourceMaster;
    HMResourceMasterSpeciality          masterSpecialty;
    HMSpecialtyCode                     specialty;
    RefRecId                            resourceRecId;

    str     specialtyCode;
    boolean isFirst = true;

    appointment = HMResourceSchedulingAppointment::find(_hmcaseTrans.HMResourceSchedulingAppointment);

    if (appointment)
    {
        planningObject = HMResourceSchedulingPlanningObject::find(appointment.HMResourceSchedulingPlanningObject);
        resourceRecId  = planningObject.HMResourceMaster;
    }
    else
    {
        hmCase = HMCase::find(_hmcaseTrans.HMCase);
        resourceRecId = hmCase.HMResourceMaster;
    }

    while select masterSpecialty
        where masterSpecialty.HMResource == resourceRecId
    join specialty
        where specialty.RecId == masterSpecialty.HMSpecialtyCode
    {
        if (isFirst)
        {
            isFirst         = false;
            specialtyCode   = specialty.SpecialtyCodeId;
        }
        else
        {
            specialtyCode   += ', ' + specialty.SpecialtyCodeId;
        }
    }

    return specialtyCode;
}

        public display str getSpecialtyDescription(HMCaseTrans _hmcaseTrans)
        {
            HMCase                              hmCase;
            HMResourceSchedulingAppointment     appointment;
            HMResourceSchedulingPlanningObject  planningObject;
            HMResourceMaster                    resourceMaster;
            HMResourceMasterSpeciality          masterSpecialty;
            HMSpecialtyCode                     specialty;
            RefRecId                            resourceRecId;

            str     specialtyDescription;
            boolean isFirst = true;

            appointment = HMResourceSchedulingAppointment::find(_hmcaseTrans.HMResourceSchedulingAppointment);

            if (appointment)
            {
                planningObject = HMResourceSchedulingPlanningObject::find(appointment.HMResourceSchedulingPlanningObject);
                resourceRecId  = planningObject.HMResourceMaster;
            }
            else
            {
                hmCase = HMCase::find(_hmcaseTrans.HMCase);
                resourceRecId = hmCase.HMResourceMaster;
            }

            while select masterSpecialty
				where masterSpecialty.HMResource == resourceRecId
			join specialty
				where specialty.RecId == masterSpecialty.HMSpecialtyCode
            {
                if (isFirst)
                {
                    isFirst                 = false;
                    specialtyDescription    = specialty.Description;
                }
                else
                {
                    specialtyDescription    += ', ' + specialty.Description;
                }
            }

            return specialtyDescription;
        }

