        public display date paymentDate (HMCaseTrans _hmcaseTrans)
        {
            HMCaseTransDetail   HMCaseTransDetail;

            select firstonly createdDateTime from HMCaseTransDetail
				order by createdDateTime desc
				where HMCaseTransDetail.HMCaseTrans == _hmcaseTrans.RecId
					&& (HMCaseTransDetail.TransactionType == HMCaseTransDetailTransactionType::InsurancePayment
					|| HMCaseTransDetail.TransactionType == HMCaseTransDetailTransactionType::PatientPayment);

            return DateTimeUtil::date(HMCaseTransDetail.createdDateTime);
        }

/// <summary>
///
/// </summary>
/// <returns></returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    if (StartDate.dateValue() == dateNull())
    {
        warning(strFmt("@SYS26332","@SYS24050"));
    }

    if (EndDate.dateValue() == dateNull())
    {
        warning(strFmt("@SYS26332","@SYS36680"));
    }

    if (EndDate.dateValue() < StartDate.dateValue())
    {
        warning("@MAZ2029");
    }
    else
    {
        element.updateData();
    }

    return ret;
}

