[Form]
public class HMChangeCaseTypeDropDialog extends FormRun
{
    HMCaseNo    selectedCaseNo;
    NoYes       isModified;
}

public void closeOk()
{
    HMCase  hmCase;
    HMCaseNotes hmCaseNotes;
    NoYes       isChanged;


    if (isModified && strLen(Notes.valueStr()) > 0)
    {
        if (element.validate())
        {
            isChanged = HMCaseManagement::ChangeCaseType(selectedCaseNo, HMCaseType.selection(), Notes.valueStr());
            //Zain -- recalculate the price on case change
            HMCaseManagement::getPrice(HMCase::findByCase(selectedCaseNo).RecId);
            //Zain

            if (isChanged)
                info("Case type changed successfully.");


        }
        else
            warning("Case type cannot be changed it has transactions present.");
    }
    else
        warning("Please, select Case type and enter notes for change.");




    super();

}

public void init()
{
    HMCase  hmCase;

    if (element.args().record().RecId)
    {
        super();
        hmCase = HMCase::find(element.args().record().RecId);
        selectedCaseNo = hmCase.CaseNo;

        HMCaseType.selection(hmCase.HMCaseType);
    }
    else
        error("Form cannot be opened with invalid record.");

}

private NoYes validate()
{
    NoYes               ret;

    ret = HMCaseManagement::ValidateChangeCaseType(selectedCaseNo);

    return ret;
}

/// <summary>
///
/// </summary>
/// <returns></returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    isModified = ret;

    return ret;
}
