Method.Source:
public void insertActionAndNotes(RecId _hmClaimTable)
{
    HMClaimNotes.Notes          = NotesString.text();
    HMClaimNotes.HMClaimTable   = _hmClaimTable;
    HMClaimNotes.insert();
}

public void updateClaimTable(HMClaimTable _hmClaimTable)
{
    _hmClaimTable.HMClaimLatestNote_Internal             = NotesString.text();
    _hmClaimTable.HMClaimLatestNoteDate_Internal         = HMClaimNotes.ModifiedDateTime;
    _hmClaimTable.HMClaimLatestNoteCreatedBy_Internal    = HMClaimNotes.ModifiedBy;
    _hmClaimTable.update();
}

/// <summary>
/// The methods saves the notes against claim for which it was added and also update fields on the claim table.
/// </summary>
public void clicked()
{
    FormDataSource          formDataSource;
    FormRun                 formRun;
    HMClaimTable            hmClaimTable;
    MultiSelectionHelper    helper = MultiSelectionHelper::construct();

    formRun = element.args().caller();

    super();

    helper.parmDatasource(element.args().record().dataSource());

    if(formRun.args().menuItemName() == "HMAllClaims")
    {
        formDataSource = formRun.dataSource("HMClaimNotes");
    }
    else if(formRun.args().menuItemName() == "HMMyRecentNote")
    {
        formDataSource = formRun.dataSource("HMClaimNotes");
    }
    else
    {
        formDataSource = formRun.dataSource(1);
    }

    hmClaimTable = helper.getFirst();

    while (hmClaimTable.RecId != 0)
    {

        if(NotesString.text() != '')
        {
            element.insertActionAndNotes(hmClaimTable.RecId);

            ttsBegin;

            element.updateClaimTable(hmClaimTable);

            ttsCommit;

        }

        hmClaimTable = helper.getNext();
    }

    formDataSource.research();
    formDataSource.refresh();
    element.close();
}
