/// <summary>
/// Applies effective date filter selected.
/// </summary>
/// <param name="_filterOptions">
/// Date filter option parameter.
/// </param>
/// <param name="_showAsOfDateTime">
/// Effective date time for a filter.
/// </param>
public void applyFilter()
{        
    utcDateTime         maximumDate, minimumDate;
    QueryBuildRange qbrValidFrom;
    str queryRangeStr='';

    if(Filter.selection() == HMActiveAll::Active)
    {
        minimumdate = DateTimeUtil::minValue();
        maximumDate = DateTimeUtil::maxValue();

        HMClaimVersion_ds.validTimeStateAutoQuery(ValidTimeStateAutoQuery::AsOfDate);
        HMClaimVersion_ds.query().validTimeStateAsOfDateTime(maximumDate);
        //HMClaimVersion_ds.validTimeStateUpdate(ValidTimeStateUpdate::Correction);
    }
    else if(Filter.selection() == HMActiveAll::All)
    {
        minimumdate = DateTimeUtil::minValue();
        maximumDate = DateTimeUtil::maxValue();

        HMClaimVersion_ds.validTimeStateAutoQuery(ValidTimeStateAutoQuery::DateRange);
        HMClaimVersion_ds.query().validTimeStateDateTimeRange(minimumDate, maximumDate);
       // HMClaimVersion_ds.validTimeStateUpdate(ValidTimeStateUpdate::Correction);
    }





/*    qbrValidFrom = SysQuery::findOrCreateRange(HMClaimVersion_ds.queryBuildDataSource(), fieldNum(HMClaimVersion,ValidFrom));

    queryRangeStr = '(%1.%3 <= %4)';

    qbrValidFrom.value(
                        strFmt(queryRangeStr,
                            HMClaimVersion_ds.queryBuildDataSource().name(),
                            fieldStr(HMClaimVersion,ValidFrom),
                            fieldStr(HMClaimVersion,ValidTo),
                            DateTimeUtil::toStr(DateTimeUtil::utcNow())));*/

    //jHMClaimTable_DS.date
   // HMClaimTable_ds.executeQuery();
}

