public void loadInsuredData(hmpatient _hmPatient)
{
    HMPatient hmPatient;
    CustTable   custTable;
    DirPartyTable dirPartyTable;
    DirPerson   dirPerson;

    LogisticsPostalAddress logisticsPostalAddress;
    LogisticsElectronicAddress logisticsElectronicAddress;

    HMPatientInsurance  hmPatientInsurance = _hmPatient.getPrimaryPatientInsurance();

    if(hmPatientInsurance.HMPatientSubscriber)
    {
        select * from hmPatient
            join * from custTable
            join * from dirPartyTable
            join * from dirPerson
            where  hmPatient.RecId == hmPatientInsurance.HMPatientSubscriber
            && hmPatient.CustTable == custTable.RecId
            && custTable.Party == dirPartyTable.RecId
            && dirPartyTable.RecId == dirPerson.RecId;
    }
    else
    {
        select * from hmPatient
            join * from custTable
            join * from dirPartyTable
            join * from dirPerson
            where  hmPatient.RecId == hmPatientInsurance.HMPatient
            && hmPatient.CustTable == custTable.RecId
            && custTable.Party == dirPartyTable.RecId
            && dirPartyTable.RecId == dirPerson.RecId;
    }

    logisticsPostalAddress = DirParty::primaryPostalAddress(dirPartyTable.RecId);
    logisticsElectronicAddress = DirParty::primaryElectronicAddress(dirPartyTable.RecId,LogisticsElectronicAddressMethodType::Phone);

    HMClaimTableDetails.InsuredName         = hmPatient.patientLastFirstMiddleName();
    //HMClaimTableDetails.InsuredBirthDay     = dirPerson.BirthDay;
    //HMClaimTableDetails.InsuredBirthMonth   = dirPerson.BirthMonth;
    //HMClaimTableDetails.InsuredBirthYear    = dirPerson.BirthYear;
    HMClaimTableDetails.HMInsuredBirthDate = mkDate(dirPerson.BirthDay, dirPerson.BirthMonth, dirPerson.BirthYear);

    switch(hmPatient.Gender)
    {
        case HcmPersonGender::Male:
            HMClaimTableDetails.InsuredGender = Gender::Male;
            break;
        case HcmPersonGender::Female:
            HMClaimTableDetails.InsuredGender = Gender::Female;
            break;
        default:
            HMClaimTableDetails.InsuredGender = Gender::Unknown;
            break;
    }

    HMClaimTableDetails.InsuredStreet = logisticsPostalAddress.Street;
    HMClaimTableDetails.InsuredCity = logisticsPostalAddress.City;
    HMClaimTableDetails.InsuredState = logisticsPostalAddress.State;
    HMClaimTableDetails.InsuredZipCode = logisticsPostalAddress.ZipCode;

    HMClaimTableDetails.InsuredPhone = logisticsElectronicAddress.Locator;
    HMClaimTableDetails.InsuredPhoneLocal = logisticsElectronicAddress.LocatorExtension;

    HMClaimTableDetails.InsuredPlanProgramName = hmPatientInsurance.PolicyGroup;
    HMClaimTableDetails.InsuredEmployersSchoolName = hmPatient.Employer;
    HMClaimTableDetails.InsuredPolicyGroupNumber = hmPatientInsurance.PolicyNumber;

    if(hmPatientInsurance)
    {
        switch(hmPatientInsurance.SubscriberRelation)
        {
            case PatientInsuranceRelation::Self:
                HMClaimTableDetails.PatientInsuredRelation = HMClaimPatientInsuredRelation::Self;
                break;
            case PatientInsuranceRelation::Daughter:
            case PatientInsuranceRelation::DependentChild:
            case PatientInsuranceRelation::Son:
                HMClaimTableDetails.PatientInsuredRelation = HMClaimPatientInsuredRelation::Child;
                break;
            case PatientInsuranceRelation::Spouse:
                HMClaimTableDetails.PatientInsuredRelation = HMClaimPatientInsuredRelation::Spouse;
                break;
            case PatientInsuranceRelation::None:
                HMClaimTableDetails.PatientInsuredRelation = HMClaimPatientInsuredRelation::None;
                break;
            default:
                HMClaimTableDetails.PatientInsuredRelation = HMClaimPatientInsuredRelation::Other;
                break;
        }
    }
    else
    {
        HMClaimTableDetails.PatientInsuredRelation = HMClaimPatientInsuredRelation::None;
    }
}

public void loadOtherInsuredData(hmpatient _hmPatient)
{
    HMPatient hmPatient;
    CustTable   custTable;
    DirPartyTable dirPartyTable;
    DirPerson   dirPerson;

    HMPatientInsurance  hmPatientInsurance = _hmPatient.getSecondaryPatientInsurance();


    if(hmPatientInsurance.HMPatientSubscriber)
    {
        select * from hmPatient
            join * from custTable
            join * from dirPartyTable
            join * from dirPerson
            where  hmPatient.RecId == hmPatientInsurance.HMPatientSubscriber
            && hmPatient.CustTable == custTable.RecId
            && custTable.Party == dirPartyTable.RecId
            && dirPartyTable.RecId == dirPerson.RecId;
    }
    else
    {
        select * from hmPatient
            join * from custTable
            join * from dirPartyTable
            join * from dirPerson
            where  hmPatient.RecId == hmPatientInsurance.HMPatient
            && hmPatient.CustTable == custTable.RecId
            && custTable.Party == dirPartyTable.RecId
            && dirPartyTable.RecId == dirPerson.RecId;
    }

    HMClaimTableDetails.OtherInsuredName = hmPatient.patientLastFirstMiddleName();
    //HMClaimTableDetails.OtherInsuredBirthDay = dirPerson.BirthDay;
    //HMClaimTableDetails.OtherInsuredBirthMonth = dirPerson.BirthMonth;
    //HMClaimTableDetails.OtherInsuredBirthYear = dirPerson.BirthYear;
    HMClaimTableDetails.HMSecondaryInsuredBirthDate = mkDate(dirPerson.BirthDay, dirPerson.BirthMonth, dirPerson.BirthYear);

    switch(hmPatient.Gender)
    {
        case HcmPersonGender::Male:
            HMClaimTableDetails.OtherInsuredGender = Gender::Male;
            break;
        case HcmPersonGender::Female:
            HMClaimTableDetails.OtherInsuredGender = Gender::Female;
            break;
        default:
            HMClaimTableDetails.OtherInsuredGender = Gender::Unknown;
            break;
    }

    HMClaimTableDetails.OtherInsuredPlanProgramName = hmPatientInsurance.PolicyGroup;
    HMClaimTableDetails.OtherInsuredEmployersSchoolName = hmPatient.Employer;
    HMClaimTableDetails.OtherInsuredPolicyGroupNumber = hmPatientInsurance.PolicyNumber;

    if(hmPatientInsurance)
    {
        HMClaimTableDetails.PatientAnotherPlan = NoYes::Yes;
    }
    else
    {
        HMClaimTableDetails.PatientAnotherPlan = NoYes::No;
    }

    this.updateOtherInsured();
}

public void loadPatientData()
{
    HMPatient hmPatient;
    CustTable   custTable;
    DirPartyTable dirPartyTable;
    DirPerson   dirPerson;

    LogisticsPostalAddress logisticsPostalAddress;
    LogisticsElectronicAddress logisticsElectronicAddress;

    select firstOnly * from hmPatient where hmPatient.RecId == HMClaimTable.HMPatient
        join * from custTable where custTable.RecId == hmPatient.CustTable
        join * from dirPartyTable where dirPartyTable.RecId == custTable.Party
        join * from dirPerson where dirPerson.RecId == dirPartyTable.RecId;

    logisticsPostalAddress = DirParty::primaryPostalAddress(dirPartyTable.RecId);
    logisticsElectronicAddress = DirParty::primaryElectronicAddress(dirPartyTable.RecId,LogisticsElectronicAddressMethodType::Phone);

    HMClaimTableDetails.PatientName = hmPatient.patientLastFirstMiddleName();
    //HMClaimTableDetails.PatientBirthDay = dirPerson.BirthDay;
    //HMClaimTableDetails.PatientBirthMonth = dirPerson.BirthMonth;
    //HMClaimTableDetails.PatientBirthYear = dirPerson.BirthYear;
    HMClaimTableDetails.HMPatientBirthDate = mkDate(dirPerson.BirthDay, dirPerson.BirthMonth, dirPerson.BirthYear);

    switch(hmPatient.Gender)
    {
        case HcmPersonGender::Male:
            HMClaimTableDetails.PatientGender = Gender::Male;
            break;
        case HcmPersonGender::Female:
            HMClaimTableDetails.PatientGender = Gender::Female;
            break;
        default:
            HMClaimTableDetails.PatientGender = Gender::Unknown;
            break;
    }

    switch(dirPerson.MaritalStatus)
    {
        case DirPersonMaritalStatus::Single:
            HMClaimTableDetails.PatientMaritalStatus = HMClaimMaritalStatus::Single;
            break;
        case DirPersonMaritalStatus::Married:
            HMClaimTableDetails.PatientMaritalStatus = HMClaimMaritalStatus::Married;
            break;
        case DirPersonMaritalStatus::None:
            HMClaimTableDetails.PatientMaritalStatus = HMClaimMaritalStatus::None;
            break;
        default:
            HMClaimTableDetails.PatientMaritalStatus = HMClaimMaritalStatus::Other;
            break;
    }

    switch(hmPatient.ProfessionalStatus)
    {
        case HMProfessionalStatus::Employed:
            HMClaimTableDetails.PatientEmployementStatus = HMClaimEmployementStatus::Employed;
            break;
        case HMProfessionalStatus::FullTimeStudent:
            HMClaimTableDetails.PatientEmployementStatus = HMClaimEmployementStatus::FullTimeStudent;
            break;
        case HMProfessionalStatus::PartTimeStudent:
            HMClaimTableDetails.PatientEmployementStatus = HMClaimEmployementStatus::PartTimeStudent;
            break;
        case HMProfessionalStatus::None:
            HMClaimTableDetails.PatientEmployementStatus = HMClaimEmployementStatus::None;
            break;
    }

    HMClaimTableDetails.PatientStreet = logisticsPostalAddress.Street;
    HMClaimTableDetails.PatientCity = logisticsPostalAddress.City;
    HMClaimTableDetails.PatientState = logisticsPostalAddress.State;
    HMClaimTableDetails.PatientZipCode = logisticsPostalAddress.ZipCode;

    HMClaimTableDetails.PatientPhone = logisticsElectronicAddress.Locator;
    HMClaimTableDetails.PatientPhoneLocal = logisticsElectronicAddress.LocatorExtension;

    this.loadInsuredData(hmPatient);
    this.loadOtherInsuredData(hmPatient);

    HMClaimTableDetails_ds.refresh();
}

