public void updateChargesPriorityButtons()
{
    Priority maxPriority = HMClaimChargeLine::getAvailablePriority(HMClaimTableDetails.RecId) - 1;

    if( !HMClaimChargeLine.RecId)
    {
        HMClaimChargeLineMoveUp.enabled(false);
        HMClaimChargeLineMoveDown.enabled(false);
    }
    else
    {
        if (HMClaimChargeLine.Priority == 1)
        {
            HMClaimChargeLineMoveUp.enabled(false);
        }
        else
        {
            HMClaimChargeLineMoveUp.enabled(true);
        }

        if (HMClaimChargeLine.Priority == maxPriority)
        {
            HMClaimChargeLineMoveDown.enabled(false);
        }
        else
        {
            HMClaimChargeLineMoveDown.enabled(true);
        }
    }
}

public void updateDiagnosisPriorityButtons()
{
    Priority maxPriority = HMClaimDiagnosis::getAvailablePriority(HMClaimTable.RecId) - 1;

    if(!HMClaimDiagnosis.RecId)
    {
        HMClaimDiagnosisMoveUp.enabled(false);
        HMClaimDiagnosisMoveDown.enabled(false);
    }
    else
    {
        if(HMClaimDiagnosis.Priority == 1)
        {
            HMClaimDiagnosisMoveUp.enabled(false);
        }
        else
        {
            HMClaimDiagnosisMoveUp.enabled(true);
        }

        if(HMClaimDiagnosis.Priority == maxPriority)
        {
            HMClaimDiagnosisMoveDown.enabled(false);
        }
        else
        {
            HMClaimDiagnosisMoveDown.enabled(true);
        }
    }
}

