public void lookup()
{
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(CustTable),this);
    Query query = New Query();
    QueryBuildDataSource queryBuildDataSource = query.addDataSource(tableNum(CustTable));

    QueryBuildRange queryBuildRange;

    sysTableLookup.addLookupfield(fieldNum(CustTable, AccountNum));
    sysTableLookup.addLookupMethod('name');

    queryBuildRange = queryBuildDataSource.addRange(fieldNum(CustTable, CustType));
    queryBuildRange.value(queryValue(CustType::Insurance));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public boolean modified()
{
    boolean ret;
    CustTable cust;

    ret = super();

    if(ret)
    {
        select firstOnly * from cust where cust.AccountNum == this.text();

        HMClaimTable.HMInsuranceCarrier = HMInsuranceCarrier::findByCode(this.text()).RecId;

        HMClaimTableDetails.InsuranceCarrierName = cust.name();
        HMClaimTableDetails.InsuranceCarrierAddress = cust.address();

        HMClaimTableDetails_ds.refresh();
    }

    return ret;
}

