void clicked()
{
    Args                            args = new Args();
    HMResourceSchedulingAppointment appointment;
    HMPatientChargeEntryTable       chargeEntryTable;

    super();

    chargeEntryTable    = HMPatientChargeEntryTable::find(HMClaimTable.HMPatientChargeEntryTable);
