private void fillBuildingUnits(TreeItemIdx _RecId,int64 _RecIdx)
{
    TreeItemIdx                     parentItemIdx;
    boolean                         hasChild;
    HMBuildingUnit                  hmBuildingUnitlocalForTree;

    hasChild = false;

    while select RecId, Description from hmBuildingUnitlocalForTree
        where hmBuildingUnitlocalForTree.RecId == _RecIdx
    {
        parentItemIdx = SysFormTreeControl::addTreeItem(Tree,hmBuildingUnitlocalForTree.Description,_RecId,hmBuildingUnitlocalForTree.RecId,imageRes,0);
        hmTreeSearchingObject.insertItem(parentItemIdx, hmBuildingUnitlocalForTree.Description);
    }
}

