public void IsNursingUnit(recId  _hmOrganizationalUnitRecId)
{
    HMOrganizationalUnit            hmOrganizationalUnit;
    HMOrganizationalUnit            hmOrganizationalUnitlocalForChildCheck;
    HMOrganizationalUnit            hmOrganizationUnitForCheck;
    HMOrganizationaCategory         hmOrganizationaCategory;
    boolean                         hasOrganizationalChildUnit;
    boolean                         hasBuildingUnit;

    hasOrganizationalChildUnit = false;
    hasBuildingUnit = false;

    while select HMOrganizationalUnit, RecId  from hmOrganizationalUnit
    where hmOrganizationalUnit.HMOrganizationalUnit == _hmOrganizationalUnitRecId
    {
            while select * from hmOrganizationalUnitlocalForChildCheck
            where hmOrganizationalUnitlocalForChildCheck.HMOrganizationalUnit == hmOrganizationalUnit.RecId
            {
            hasOrganizationalChildUnit = true;
            }
            if(hasOrganizationalChildUnit)
            {
                select count(recId) from hmOrganizationUnitForCheck
                join * from hmOrganizationaCategory
                where hmOrganizationUnitForCheck.HMOrganizationaCategory == hmOrganizationaCategory.RecId
                && hmOrganizationUnitForCheck.RecId == hmOrganizationalUnit.RecId
                && hmOrganizationaCategory.Indicator == HMOrganizationalCategoryIndicator::NursingAssignments;
                if(hmOrganizationUnitForCheck.RecId>0)
                {
                    hasNursingUnit=true;

                }
                this.IsNursingUnit(hmOrganizationalUnit.RecId);
                hasOrganizationalChildUnit = false;
            }
            else
            {
                select count(recId) from hmOrganizationUnitForCheck
                join * from hmOrganizationaCategory
                where hmOrganizationUnitForCheck.HMOrganizationaCategory == hmOrganizationaCategory.RecId
                && hmOrganizationUnitForCheck.RecId == hmOrganizationalUnit.RecId
                && hmOrganizationaCategory.Indicator == HMOrganizationalCategoryIndicator::NursingAssignments;
                if(hmOrganizationUnitForCheck.RecId>0)
                {
                     hasNursingUnit=true;
                }
            }

    }




}

public boolean isPatternValid(str _pattern, str _strToTest)
{
    System.Text.RegularExpressions.Regex regEx;
    System.Text.RegularExpressions.Match regMatch;
    InteropPermission permission = new InteropPermission(InteropKind::ClrInterop);
    boolean retVal;
    permission.assert();
    //BP Deviation documented
    regEx = new System.Text.RegularExpressions.Regex(_pattern);

    regMatch = regEx.Match(_strToTest);
    retVal = regMatch.get_Success();

    return retVal;
}

