        RefProvider.visible(false);
        RegistrationDetails.visible(false);
        if (staffMember == HMStaffMemberType::Nurse)
        {
             this.design().caption("@MAZ5880");
        }
        else if (staffMember == HMStaffMemberType::Other)
        {
            this.design().caption(SysLabel::labelId2String2('@SYS115968'));
        }
        else if (staffMember == HMStaffMemberType::Driver)
