public int active()
{
    int ret;
    HMClinicStaffRole             hmClinicStaffRole;

    ret = super();

    if(ret)
    {
        hmClinicStaffRole = HMClinicStaffRole::find(HMClinicStaff.HMClinicStaffRole);
        if (hmClinicStaffRole.Role == HMStaffMemberType::Nurse || hmClinicStaffRole.Role == HMStaffMemberType::NursePractitioner)
        {
            NurseQualification.allowEdit(true);
            NurseQualification.enabled(true);
            NursingUnit.enabled(true);
            if(HMClinicStaff.RecId)
            {
                DetailsGroup.enabled(true);
                NursingUnitActionPane.enabled(true);
                element.parentCall();
            }
        }
        else
        {
            NurseQualification.allowEdit(false);
            NurseQualification.enabled(false);
            NursingUnit.enabled(true);
            DetailsGroup.enabled(true);
            NursingUnitActionPane.enabled(true);
        }

        staffRecId = str2int64(StaffType.valueStr());
        RefProvider.text(HMClinicStaff::getPhysicianNameByRecId(HMClinicStaff.RefProvider));

    }
    return ret;
}

