public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Common ret;
    SysReferenceTableLookup sysReferenceTableLookup;
    QueryBuildDataSource    queryBuildDataSource;

    Query   query           = new Query();
    sysReferenceTableLookup = SysReferenceTableLookup::newParameters(tableNum(HMClinicStaffRole), _formReferenceControl, true);

    queryBuildDataSource  = query.addDataSource(tableNum(HMClinicStaffRole));

    switch (staffMember)
    {
        case HMStaffMemberType::Nurse:
            queryBuildDataSource.addRange(fieldNum(HMClinicStaffRole, Role)).value(SysQuery::value(HMStaffMemberType::Nurse));
            queryBuildDataSource.addRange(fieldNum(HMClinicStaffRole, Role)).value(SysQuery::value(HMStaffMemberType::NursePractitioner));
            break;

        case HMStaffMemberType::Physician:
            queryBuildDataSource.addRange(fieldNum(HMClinicStaffRole, Role)).value(SysQuery::value(HMStaffMemberType::Physician));
            break;

        case HMStaffMemberType::Driver:
            queryBuildDataSource.addRange(fieldNum(HMClinicStaffRole, Role)).value(SysQuery::value(HMStaffMemberType::Driver));
            break;

        default:
            queryBuildDataSource.addRange(fieldNum(HMClinicStaffRole,Role)).value(SysQuery::value(HMStaffMemberType::Administration));
            queryBuildDataSource.addRange(fieldNum(HMClinicStaffRole,Role)).value(SysQuery::value(HMStaffMemberType::Other));
            queryBuildDataSource.addRange(fieldNum(HMClinicStaffRole,Role)).value(SysQuery::value(HMStaffMemberType::Staff));
            queryBuildDataSource.addRange(fieldNum(HMClinicStaffRole,Role)).value(SysQuery::value(HMStaffMemberType::Technician));
    }

    sysReferenceTableLookup.addLookupfield(fieldNum(HMClinicStaffRole, Code));
    sysReferenceTableLookup.addLookupfield(fieldNum(HMClinicStaffRole, Description));
    sysReferenceTableLookup.parmQuery(query);

    ret = sysReferenceTableLookup.performFormLookup();

  //  ret = super(_formReferenceControl);
    return ret;
}

