public void written()
{
    HMStaffRoleTypeIdentification       hmStaffRoleTypeIdentificationCheck;
    HMStaffRoleTypeIdentification       hmStaffRoleTypeIdentificationUpdate;

    super();

    if(HMStaffRoleTypeIdentification.IsEPrescriptionID == NoYes::Yes)
    {
        select RecId from hmStaffRoleTypeIdentificationCheck
            where hmStaffRoleTypeIdentificationCheck.IsEPrescriptionID == NoYes::Yes
            && hmStaffRoleTypeIdentificationCheck.RecId != HMStaffRoleTypeIdentification.RecId
            && hmStaffRoleTypeIdentificationCheck.HMClinicStaff == HMStaffRoleTypeIdentification.HMClinicStaff;

        if(hmStaffRoleTypeIdentificationCheck.RecId)
        {
            ttsBegin;

            hmStaffRoleTypeIdentificationUpdate = HMStaffRoleTypeIdentification::find(hmStaffRoleTypeIdentificationCheck.RecId,true);
            hmStaffRoleTypeIdentificationUpdate.IsEPrescriptionID = NoYes::No;
            hmStaffRoleTypeIdentificationUpdate.HMePrescriptionIdentificationType = 0;
            hmStaffRoleTypeIdentificationUpdate.update();

            ttsCommit;

            this.research();
        }
    }
}

