public void init()
{
    super();

    switch (staffMember)
    {
        case HMStaffMemberType::Nurse:
            this.query().dataSourceTable(tableNum(HMClinicStaffRole)).addRange(fieldNum(HMClinicStaffRole, Role)).value(SysQuery::value(HMStaffMemberType::Nurse));
            this.query().dataSourceTable(tableNum(HMClinicStaffRole)).addRange(fieldNum(HMClinicStaffRole, Role)).value(SysQuery::value(HMStaffMemberType::NursePractitioner));
            break;

        case HMStaffMemberType::Physician:
             this.query().dataSourceTable(tableNum(HMClinicStaffRole)).addRange(fieldNum(HMClinicStaffRole,Role)).value(SysQuery::value(HMStaffMemberType::Physician));
            break;

        case HMStaffMemberType::Driver:
             this.query().dataSourceTable(tableNum(HMClinicStaffRole)).addRange(fieldNum(HMClinicStaffRole,Role)).value(SysQuery::value(HMStaffMemberType::Driver));
            break;

        default:
            this.query().dataSourceTable(tableNum(HMClinicStaffRole)).addRange(fieldNum(HMClinicStaffRole,Role)).value(SysQuery::value(HMStaffMemberType::Administration));
            this.query().dataSourceTable(tableNum(HMClinicStaffRole)).addRange(fieldNum(HMClinicStaffRole,Role)).value(SysQuery::value(HMStaffMemberType::Other));
            this.query().dataSourceTable(tableNum(HMClinicStaffRole)).addRange(fieldNum(HMClinicStaffRole,Role)).value(SysQuery::value(HMStaffMemberType::Staff));
            this.query().dataSourceTable(tableNum(HMClinicStaffRole)).addRange(fieldNum(HMClinicStaffRole,Role)).value(SysQuery::value(HMStaffMemberType::Technician));
    }
}

