public boolean validate()
{
    boolean ret;

    ret = super();

    if (ret && HcmWorker::findByPersonnelNumber(this.text()).RecId != 0)
    {
        ret = ret && checkFailed("@SYS327962");
    }
    if (!ret)
    {
        this.text(personnelNumberIdOldValue);
    }

    return ret;
}

public boolean modified()
{
    boolean ret;
    HMSalutation hmSalutation;

    ret = super();

    if (ret)
    {
        hmSalutation = HMSalutation::findBySalutation(this.selection());
        if(hmSalutation && conLen(hmSalutation.Gender) == 1)
        {
            DirPerson_Gender.selectText(conPeek(hmSalutation.Gender,1));
            HMClinicStaff.Gender = DirPerson_Gender.selection();
        }
        else
        {
            HMClinicStaff.Gender = 0;
        }
    }

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if(ret)
    {
        if (HMClinicStaffRole::find(HMClinicStaff.HMClinicStaffRole).Role == HMStaffMemberType::Nurse || HMClinicStaffRole::find(HMClinicStaff.HMClinicStaffRole).Role == HMStaffMemberType::NursePractitioner)
        {
            NurseQualification.enabled(true);
        }
        else
        {
            HMClinicStaff.HMNurseQualification = 0;
            NurseQualification.enabled(false);
        }

        staffRecId = str2int64(this.valueStr());
    }
    return ret;
}

