public boolean modified()
{
    HMClinicStaff           hmClinicStaffLocal;
    HMlookupClass           HMlookupClassObj = new HMlookupClass();

    boolean ret;

    ret = super();

    if (ret)
    {
        if (this.text() != "")
        {
            hmClinicStaffLocal =  HMlookupClassObj.checkValidPhysicianName(this.text());

            if (hmClinicStafflocal.RecId)
            {
                element.storePhysician(hmClinicStafflocal.RecId);
            }
            else
            {
                warning(strFmt("@SYS316757",'Physician'));
                this.text(HMClinicStaff::getPhysicianNameByRecId(HMClinicStaff.RefProvider));
            }
        }
        else
        {
            element.storePhysician(0);
        }

    }
    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        if (this.value() == true && logisticsElectronicAddress.otherPrimaryExists())
        {
            if (Box::yesNo("@SYS304427", DialogButton::No) == DialogButton::No)
            {
                ret = false;
            }
            else
            {
                IsDuplicateContactExist = true;
            }
        }
    }

    return ret;
}

void clicked()
{
    HMStaffNursingUnit          hmStaffNursingUnitLocal;

    hmStaffNursingUnitLocal.HMClinicStaff=HMClinicStaff.RecId;
    hmStaffNursingUnitLocal.HMOrganizationalUnit=organizationalUnitRecId;
    hmStaffNursingUnitLocal.insert();
    super();

    HMStaffNursingUnit_ds.executeQuery();




}

public void selectionChanged(FormTreeItem _OldItem, FormTreeItem _NewItem, FormTreeSelect _how)
{
    FormTreeItem                 selectedTreeItem;
    HMOrganizationalUnit         hmOrganizationalingUnit;
    HMOrganizationaCategory      hmOrganizationaCategory;


    super(_OldItem, _NewItem, _how);

    selectedTreeItem = Tree.getItem(Tree.getSelection());
    data = selectedTreeItem.data();
    organizationalUnitRecId=str2int64(data);

    select HMOrganizationaCategory, RecId from hmOrganizationalingUnit
    join RecId, Indicator from hmOrganizationaCategory
        where hmOrganizationalingUnit.HMOrganizationaCategory == hmOrganizationaCategory.RecId
        && hmOrganizationalingUnit.RecId == organizationalUnitRecId;

    if(hmOrganizationalingUnit.RecId)
    {
        if(hmOrganizationaCategory.Indicator == HMOrganizationalCategoryIndicator::DepartmentalUnit)
        {
            SelectButton.enabled(false);
        }
        else
        {
            SelectButton.enabled(true);
        }
    }
    else
    {
        SelectButton.enabled(false);
    }



}

