public boolean isFileNameOk()
{
    Filename    onlyname;
    str 1       dummy;
    str         ext;

    [dummy, onlyname, ext] = fileNameSplit(Filename.text());

    return onlyname != '' && ext != '';
}

public boolean isFileTypeOk()
{
    DocuFileTypes   docuFileTypes;
    Filename        onlyname;
    str 1           dummy;
    str             ext;

    [dummy, onlyname, ext] = fileNameSplit(Filename.text());

    return !docuFileTypes.isEnabled(Global::strReplace(ext, '.', ''));

    //return onlyname != '' && ext != '';
}

public void initValue()
{
    super();
    docuRef.RefTableId = tableNum(hcmWorker); //TableId, for which the attachement should be attached.
    docuRef.RefRecId = hcmWorker.RecId; //RecId, for which the attachement should be attached.
    docuRef.RefCompanyId = hcmWorker.dataAreaId; //CompanyId

}

public boolean validateWrite()
{
    boolean ret = true;
    DocuType    docuType;

    if(DocuRef.isNewRecord())
    {
        isNewRec = true;

        if(cbxDocuTypeId.selection() == 0)
        {
            warning("@MAZ2961");
            ret = false;
        }

        if(ret && Filename.text() == "")
        {
            warning("@MAZ2962");
            ret = false;
        }

        if(ret && !(element.isFileNameOk()))
        {
            warning("@MAZ2963");
            ret = false;
        }

        if(ret && !(element.isFileTypeOk()))
        {
            warning("@MAZ1317");
            ret = false;
        }
        if(ret)
        {
            if(cbxDocuTypeId.selection() == HMAttachmentDocType::ElectronicSignature)
            {
                if(element.electronicSignatureExist())
                {
                    warning("@MAZ6899");
                    ret = false;
                }
            }
        }

        if(ret)
        {
            docuRef.TypeId = element.getSelectedDocuTypeId();

            ret = super();

            if(ret)
            {
                docuType = DocuType::find(docuRef.TypeId);
                //todo:  ax7  no method docuType.getArchivePath() docuref hmdocaction
                /*
                if(docuType.FilePlace == DocuFilePlace::Archive)
                {
                    if(!HMDocuAction::checkDirectoryPermission(docuType.getArchivePath()))
                    {
                        warning("@MAZ3859");
                        ret = false;
                    }
                }
                */

            }

        }
    }
    else
    {
        ret = super();
    }

    return ret;
}

