public boolean modified()
{
    boolean ret;

    ret = super();

    if(ret && cbxDocuTypeId.selection()!=0)
    {
        FileSelection.enabled(true);
        filename.allowedit(true);
    }
    else if(cbxDocuTypeId.selection()==0)
    {
        FileSelection.enabled(false);
        filename.allowedit(false);
    }

    return ret;
}

