public boolean modified()
{
    boolean ret;
    boolean isValidated = true;
    HMSHA256   sha = new HMSHA256();

    ret = super();

    selectedFile = Filename.text();

    if(ret && this.text() != '')
    {
        if(!WinAPI::fileExists(this.text(),false))
        {
            warning("@MAZ4997");
            isValidated = false;
        }

        if(isValidated && !(element.isFileNameOk()))
        {
            warning("@MAZ2963");
            isValidated = false;
        }

        if(isValidated)
        {
            docuRef.HMHashValue  = sha.getHash(selectedFile);
            DocuRef_HMHashValue.text(docuRef.HMHashValue);
        }
    }

    return ret;
}
