public boolean canClose()
{
    boolean ret;

    if (!isRecordCreated)
    {
        numberSeq.deleteNewNumSeq();
    }
    else
    {
        numberSeq.commitNewNumSeq();
    }

    ret = super();

    return ret;
}

public void closeCancel()
{
    numberSeq.deleteNewNumSeq();
    super();
}

