    }
    else
    {
        caseNo = HMCase_CaseNo.valueStr();
        if(HMCase.validateWrite())
        {
            hmcaseRecid = HMCaseManagement::createCase(element.getHMPatientRecId(),element.getOrganizationalUnitRecid(),str2enum(hmCaseType ,HMCase_HMCaseType.valueStr()),element.getPatientinsuranceRecid(),dateNull(),HMPreAuthorizationStatus::None,'',caseNo, HMCase.HMResourceMaster, HMCase.AddmisionDate, HMCase.Notes,HMCase.TypeOfAdmission,HMCase.AdmittingDiagnosisCode, true, HMCase.HMBuildingUnit_Room, HMCase.HMPatientStatus);
            if(hmcaseRecid != 0)
            {
                ttsbegin;
                while select forupdate appointmentNumber from _hmcaseLocal where _hmcaseLocal.recid == hmcaseRecid
                {
                    _hmcaseLocal.AppointmentNumber = HMCase.AppointmentNumber;
                    _hmcaseLocal.update();
                }
                ttscommit;
                //args = new args(formstr(HMCaseDetail));
                //args.record(HMCase);
                //formRun = classfactory.formrunclass(args);
                //formRun.init();
                //formRun.run();
                //formRun.wait();
                //element.close();
                numberSeq.commitNewNumSeq();
                info("@MAZ13505");
            }
        }
        else
        {
            return;
        }
    }
    callerFormRun = element.args().caller();
    if(callerFormRun)
    {
        if (formHasMethod(callerFormRun, 'dateSearchClick'))
          {
              element.args().caller().dateSearchClick();
          }
    }

    if(HMCase.HMResourceMaster==0)
        return ;
    super();



}

