public void getPatient(Common _record, boolean _updatePatientSpecificFieldsOnly = false)
{
    boolean   ret  = false;
    HMPatient           hmPatientLocal;
    DirPartyTable       dirPartyTableLocal;
    CustTable           custTableLocal;
    HMPatient pat = _record;
    if(HMCaseManagement::validatePatientExistingCase(_record.RecId))
    {
        HMPatient.text(pat.PatientName());
        if(HMPatient.valueStr()!="")
        {
            select REcId from hmPatientLocal
            join custTableLocal
            join dirPartyTableLocal
            where hmPatientLocal.CustTable == custTableLocal.RecId
            && custTableLocal.Party == dirPartyTableLocal.RecId
            && dirPartyTableLocal.Name == HMPatient.valueStr();

            if(hmPatientLocal.RecId)
            {
                HMPatientRecId = hmPatientLocal.RecId;
            }


            //info(HMOpenCases_ds.query().toString());
        }
    }


}

