public void init()
{
    HMCaseNo    caseNo;

    super();

    numberSeq = new HMNumberSequenceHandler();

    HMOrganizationalUnitRecid = HMParameters::find().HMOrganizationalUnitERUnit;

    if(HMOrganizationalUnitRecid > 0)
    {
        OrgUnit.text(HMOrganizationalUnit::find(HMOrganizationalUnitRecid).Description);

        caseNo = numberSeq.generateNumberSequence(HMOrganizationalUnit::getOperatingUnitRecid(HMOrganizationalUnitRecid), extendedtypenum(HMCaseNo));

        hmCase_CaseNo.text(caseNo);

        if(!caseNo)
        {
            throw error(strFmt("@MAZ8513","@MAZ1375",HMOrganizationalUnit::find(HMOrganizationalUnitRecid).Description));
        }
        else
        {
            HMCase.CaseNo = caseNo;
        }


    }
}

public void run()
{

    HMCase hmcaseLocal;
    hmcasetrans hmcaseTrans;
    int totalCaseTransCount,notPaidCaseTransCount;
    if(element.args().menuItemName() == menuitemDisplayStr(HMCaseEditDialog))
    {
        Group2.allowEdit(false);
        OrgUnit.allowEdit(false);
        HMNew_Patient.enabled(false);
        hmcaseLocal = element.args().record();

        select count(RecId) from hmcaseTrans
        where hmcaseTrans.HMCase == hmcaseLocal.RecId;
        totalCaseTransCount = hmcaseTrans.RecId;

        select count(RecId) from hmcaseTrans
        where hmcaseTrans.HMCase == hmcaseLocal.RecId
        && hmcaseTrans.BillingStatus == HMBillStatus::NotPaid;
        notPaidCaseTransCount = hmcaseTrans.RecId;

        if(totalCaseTransCount != notPaidCaseTransCount)
        {
            HMPatientInsurance.allowEdit(false);
        }


        if(hmcaseLocal)
        {
            HMCase_ds.positionToRecord(hmcaseLocal);
            element.getPatient(HMPatient::find(hmcaseLocal.HMPatient));
            element.storeOrganizationalUnit(hmcaseLocal.Clinic);
            element.storeHMPatientInsurance(hmcaseLocal.HMPatientInsurance);
            HMPatientInsurance.text(HMPatientInsurance::find(hmcaseLocal.HMPatientInsurance).getCarrier().Code);
        }
    }
    else
    {
        HMCase_ds.create();
    }

    super();


}

