private void updateCaseTrans()
{
    HMCaseTrans hmcaseTrans;
    while select forupdate hmcaseTrans
        where hmcaseTrans.hmcase == HMCase.recid
    {
        hmcaseTrans.PatientResponsibility = HMPatientPaymentResponsibility::Client;
        hmcaseTrans.updateAmountAndDiscount();
        hmcaseTrans.update();
    }
}

public boolean updateInsuranceDetailsForInsuranceCode()
{
    HMPatientInsurance                      hmPatientInsuranceLocal ;
    HMInsuranceCarrier                      carrier;
    HMInsuranceContract                     contract;
    HMInsuranceContractClassOrPlans         plan;
    HMInsuranceContractCarrierCombination   combination;
    RefRecId                                recId = HMPatientRecId;

    select RecId from hmPatientInsuranceLocal
        exists join plan
        where hmPatientInsuranceLocal.HMInsuranceContractClassOrPlans == plan.RecId
            exists join contract
            where plan.HMInsuranceContract == contract.RecId
                exists join combination
                where contract.HMInsuranceContractCarrierCombination == combination.recId
                    exists join carrier
                        where combination.HMInsuranceCarrier == carrier.RecId
                            && carrier.Code == HMPatientInsurance.text()
                            && hmPatientInsuranceLocal.HMPatient == recId;//hmResourceSchedulingAppointmentForDataFetch.HMPatient;HMResourceSchedulingAppointment.HMPatient;//

    //BookingNotes.text('');
    //InsuranceName.text('');
    //InsurancePhone.text('');
    //InsuranceContract.text('');
    //InsuranceClass.text('');
    //InsuranceExpirationDate.dateValue(dateNull());

    if (hmPatientInsuranceLocal.RecId)
    {
        if(!HMPatientEligibilityLog::isPatientInsuranceEligible(hmPatientInsuranceLocal.RecId))
        {
            HMPatientInsuranceRecid = 0;
            return false;
        }
        else
        {
            HMPatientInsuranceRecid = hmPatientInsuranceLocal.RecId;
        }
    }
    else if (HMPatientInsurance.text())
    {
        HMPatientInsuranceRecid = 0;

        error("@MAZ13518");
        HMPatientInsurance.text('');

        return false;
    }
    else
    {
        HMPatientInsuranceRecid = 0;
    }

    return true;
}

