public boolean modified()
{
    boolean ret;
    RecId locationRecId;
    HMCaseNo caseNo;

    ret = super();

    if(ret)
    {
        if(this.text() == '')
        {
            element.storeOrganizationalUnit(0);
        }
        else
        {
            //Fix: Bug 3438 by Ammar.Ali for MazikCare
            //locationRecId = HMResourceSchedulingHelper::getNursingUnitLocationRecId(this.text());
            locationRecId = (select RecId from HMOrganizationalUnit
                                where HMOrganizationalUnit.Description == this.text()).RecId;
            //Fix: Bug 3438 by Ammar.Ali for MazikCare
            if(!locationRecId)
            {
                warning(strFmt("@SYS316757",this.label()));
                this.text(HMOrganizationalUnit::find(HMOrganizationalUnitRecid).Description);
            }
            else
            {
                element.storeOrganizationalUnit(locationRecId);

                //Noman
                numberSeq.deleteNewNumSeq();
                caseNo = numberSeq.generateNumberSequence(HMOrganizationalUnit::getOperatingUnitRecid(locationRecId), extendedtypenum(HMCaseNo));

                hmCase_CaseNo.text(caseNo);

                if(!caseNo)
                {
                    caseNo = '';
                    HMCase.CaseNo = caseNo;
                    throw error(strFmt("@MAZ8513","@MAZ1375",HMOrganizationalUnit::find(locationRecId).Description));
                }
                else
                {
                    HMCase.CaseNo = caseNo;
                }
                //Noman
            }
        }

        //element.storeOrganizationalUnitRecId(0);
    }

    return ret;
}

