[Form]
public class HMEmailTemplatesEventMapping extends FormRun
{
    FormStringControl fileNameControl;
}

str fileNameLookupFilename()
{
    Filename    filepath;
    Filename    filename;
    Filename    fileType;

    [filepath, filename, fileType] = fileNameSplit(fileNameControl.text());

    return filename + fileType;
}

container fileNameLookupFilter()
{
    #File
    Filename    filepath;
    Filename    filename;
    Filename    fileExtention;

    return (["All files", #AllFiles]);
}

// AOSRunMode::client
str fileNameLookupInitialPath()
{
    #WinAPI

    Filename    filepath;
    Filename    filename;
    Filename    fileType;

    [filepath, filename, fileType] = Global::fileNameSplit(fileNameControl.text());

    // Default path
    if (!filepath)
    {
        filepath = WinAPI::getFolderPath(#CSIDL_DESKTOP);
    }

    return filepath;
}

// AOSRunMode::client
str fileNameLookupTitle()
{
    return  'Lookup Title: ' + fileNameControl.label();
}

public void updateFields()
{

    HMEmailTemplatesEventMapping_ds.object(fieldNum(HMEmailTemplatesEventMapping,EmailAppointmentStatus)).enabled(true);
    HMEmailTemplatesEventMapping_ds.object(fieldNum(HMEmailTemplatesEventMapping,Period)).enabled(true);
    HMEmailTemplatesEventMapping_ds.object(fieldNum(HMEmailTemplatesEventMapping,EmailUnits)).enabled(true);
    HMEmailTemplatesEventMapping_ds.object(fieldNum(HMEmailTemplatesEventMapping,EmailRelativeDate)).enabled(true);
    //HMEmailTemplatesEventMapping_ds.object(fieldNum(HMEmailTemplatesEventMapping,EmailAppointmentStatus)).mandatory(true);
    TabPageServiceType.enabled(true);
    TabPageScheduleType.enabled(true);
    TabPageAppointmentStatus.enabled(true);

    switch(HMEmailTemplatesEventMapping.EmailAppointmentStatus)
    {
        case HMEmailAppointmentStatus::Cancelled:
            HMEmailTemplatesEventAppointmentStatus_DS.object(fieldNum(HMEmailTemplatesEventAppointmentStatus,ConfirmationStatus)).visible(false);
            HMEmailTemplatesEventAppointmentStatus_DS.object(fieldNum(HMEmailTemplatesEventAppointmentStatus,ConfirmationStatus)).mandatory(false);
            HMEmailTemplatesEventAppointmentStatus_DS.object(fieldNum(HMEmailTemplatesEventAppointmentStatus,CancellationStatus)).mandatory(true);
            HMEmailTemplatesEventAppointmentStatus_DS.object(fieldNum(HMEmailTemplatesEventAppointmentStatus,CancellationStatus)).visible(true);
            HMEmailTemplatesEventAppointmentStatus_DS.object(fieldNum(HMEmailTemplatesEventAppointmentStatus,CancellationStatus)).allowEdit(true);
            break;
        case HMEmailAppointmentStatus::Confirmed:
            HMEmailTemplatesEventAppointmentStatus_DS.object(fieldNum(HMEmailTemplatesEventAppointmentStatus,ConfirmationStatus)).visible(true);
            //HMEmailTemplatesEventAppointmentStatus_DS.object(fieldNum(HMEmailTemplatesEventAppointmentStatus,ConfirmationStatus)).mandatory(true);
            HMEmailTemplatesEventAppointmentStatus_DS.object(fieldNum(HMEmailTemplatesEventAppointmentStatus,CancellationStatus)).mandatory(false);
            HMEmailTemplatesEventAppointmentStatus_DS.object(fieldNum(HMEmailTemplatesEventAppointmentStatus,CancellationStatus)).visible(false);
            HMEmailTemplatesEventAppointmentStatus_DS.object(fieldNum(HMEmailTemplatesEventAppointmentStatus,ConfirmationStatus)).allowEdit(true);
            break;
        case HMEmailAppointmentStatus::None:
            HMEmailTemplatesEventAppointmentStatus_DS.object(fieldNum(HMEmailTemplatesEventAppointmentStatus,ConfirmationStatus)).allowEdit(false);
            HMEmailTemplatesEventAppointmentStatus_DS.object(fieldNum(HMEmailTemplatesEventAppointmentStatus,ConfirmationStatus)).visible(true);
            HMEmailTemplatesEventAppointmentStatus_DS.object(fieldNum(HMEmailTemplatesEventAppointmentStatus,CancellationStatus)).visible(false);
            HMEmailTemplatesEventAppointmentStatus_DS.object(fieldNum(HMEmailTemplatesEventAppointmentStatus,CancellationStatus)).allowEdit(false);
            HMEmailTemplatesEventAppointmentStatus_DS.object(fieldNum(HMEmailTemplatesEventAppointmentStatus,ConfirmationStatus)).mandatory(false);
            HMEmailTemplatesEventAppointmentStatus_DS.object(fieldNum(HMEmailTemplatesEventAppointmentStatus,CancellationStatus)).mandatory(false);
            break;
    }

}

public int active()
{
    int ret;

    ret = super();
    if(ret)
    {
        element.updateFields();
    }
    return ret;
}

public void create(boolean _append = false)
{
    super(_append);
    element.updateFields();
}

public void create(boolean _append = false)
{
    super(_append);
    if(HMEmailTemplatesEventAppointmentStatus.ConfirmationStatus == HMAppointmentConfirmationStatus::None)
    {
        HMEmailTemplatesEventAppointmentStatus_ds.write();
    }
}

public void gotFocus()
{
    fileNameControl = this;
    super();
}

public boolean modified()
{
    boolean ret;
    DialogButton    dialogButton;
    HMEmailTemplatesEventAppointmentStatus hmEmailTemplatesEventAppointmentStatusLocal;

    ret = super();

    if(ret && HMEmailTemplatesEventMapping.orig().EmailAppointmentStatus != HMEmailAppointmentStatus::None
    && HMEmailTemplatesEventMapping.orig().EmailAppointmentStatus != HMEmailTemplatesEventMapping.EmailAppointmentStatus)
    {
        dialogButton = Box::yesNo("@MAZ11665", DialogButton::Yes, "@SYS301622");
        if (dialogButton == DialogButton::Yes)
        {
            delete_from hmEmailTemplatesEventAppointmentStatusLocal
                where hmEmailTemplatesEventAppointmentStatusLocal.HMEmailTemplatesEventMapping == HMEmailTemplatesEventMapping.RecId;

            element.updateFields();

            HMEmailTemplatesEventAppointmentStatus_ds.executeQuery();
        }
        else
        {
            HMEmailTemplatesEventMapping.EmailAppointmentStatus = HMEmailTemplatesEventMapping.orig().EmailAppointmentStatus;
            ret = false;
        }
    }

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();
    if(ret)
    {
        if(HMEmailTemplatesEventAppointmentStatus.ConfirmationStatus == HMAppointmentConfirmationStatus::None)
        {
            HMEmailTemplatesEventAppointmentStatus_ds.write();
        }
    }
    return ret;
}
