            _inventDim =  Purchline.inventDim();

            HmImportproductreceiptdetails.warehouse = _inventDim.InventLocationId;
            HmImportproductreceiptdetails.Site = _inventDim.InventSiteId;

            HmImportproductreceiptdetails.Description = Purchline.itemName();


