public void executeQuery()
{
    DirPartyPostalAddressView::updateQuery(this.query().dataSourceName(this.name()), DirOrganization.RecId);

    super();

    this.updateButtons();

    if (postalAddressLocation != 0 && postalAddressLocation != dirPartyPostalAddressView.Location)
    {
        this.findValue(fieldnum(DirPartyPostalAddressView,Location),SysQuery::value(postalAddressLocation));
    }
}

