public void executeQuery()
{
    super();

    if (contactInfoRecId != 0 && contactInfoRecId != LogisticsElectronicAddress.RecId)
    {
        this.findValue(fieldNum(LogisticsElectronicAddress,RecId),SysQuery::value(contactInfoRecId));
    }
    contactInfoLocation = LogisticsElectronicAddress.Location;
}

public void write()
{
    ttsbegin;
    partyForm.electronicAddressPreWrite(LogisticsElectronicAddress);
    super();
    partyForm.electronicAddressPostWrite(LogisticsElectronicAddress);
    ttscommit;

    contactInfoLocation = LogisticsElectronicAddress.Location;

    this.research(true);
}

public void modified()
{
    #task

    super();

    if(IsDuplicateContactExist && LogisticsElectronicAddress.IsPrimary)
    {
        element.task(#taskSave);
        IsDuplicateContactExist = false;
    }
}

