public void approveBOM()
{
    HcmWorker worker;
    HcmEmployment employe;

    BOMVersionApprove approve = BOMRouteVersionApprove::newBOMVersion(bomVersion);

    select firstOnly RecId from worker
    join * from employe where employe.Worker == worker.RecId && employe.EmploymentType == HcmEmploymentType::Employee
    && employe.LegalEntity ==CompanyInfo::find().RecId;

    approve.parmApprover(worker.RecId);
    approve.parmApproveBOM(NoYes::Yes);

    approve.runOperation();
}

